"use strict";
var helpers = require('syracuse-core/lib/helpers');
var Box = require("syracuse-ui/lib/article/box").Box;

function EmptyBox(){
}

exports.EmptyBox = helpers.defineClass(EmptyBox, Box, {
    drawBox: function(){
        this.isEmptyBox = true;
        this.$skin = "s-aw-empty";
        this.$authoringLevel = "field";
        this.$$item = this.$$body = $(this._item = this._header = this._body = document.createElement("div"));
        this.$$container[0].appendChild(this._item);
        this.ensureLayoutMode();
    },
    ensureLayoutMode: function(){
        this._item.className = this.$skin;
        if (this.page.authoringPage) {
            this.page.authoringPage.toggleItemAuthoring(this, true);
        }
    },
    onMouseEnter: function(){
    
    },
    dispose: function(){
        this.deleteLink = null;
        Box.prototype.dispose.call(this);
    }
});
