"use strict";
var helpers = require('syracuse-core/lib/helpers');
var BarSplitter = require("syracuse-ui/lib/page/utility/barSplitter").BarSplitter;

function TreesBar() {}

exports.TreesBar = helpers.defineClass(TreesBar, BarSplitter, {
	load: function(page, layoutSlot, barSlot, barBody) {
		this.options = {
			resizeDirection: {
				right: true
			},
			$skins: {
				bar: "s-aw-treebar",
				openerPicker: "s-aw-treebar-opener",
				resizeBar: "s-aw-treebar-resizer"
			}
		};
		this.preferences = {
			isDocked: true,
			isCollapsed: false
		};
		this._barSlot = barSlot;
		this._barBody = barBody;
		BarSplitter.prototype.load.call(this, page, null, this._barSlot, this._barBody);
	}
});