"use strict";
var helpers = require('syracuse-core/lib/helpers');

function ChoiceFieldAddin() {}

exports.ChoiceFieldAddin = helpers.defineClass(ChoiceFieldAddin, null, {
	load: function(designer) {
		this.designer = designer;
		this.designer.$prototype.$properties.$choiceField$format = {
			$type: "application/x-choice",
			$title: this.designer.localize.fchoice_formatTitle,
			$value: {
				$type: "application/x-string",
				$enum: [{
					$value: "$combo",
					$title: this.designer.localize.fchoice_formatList
				}, {
					$value: "$radios",
					$title: this.designer.localize.fchoice_formatRadios
				}]
			}
		};
		this.designer.$prototype.$properties.$choiceField$choiceLayout = {
			$type: "application/x-choice",
			$title: this.designer.localize.fchoice_layout_title,
			$value: {
				$type: "application/x-string",

				$enum: [{
					$value: "row",
					$title: this.designer.localize.fchoice_layout_1_row
				}, {
					$value: "1",
					$title: this.designer.localize.fchoice_layout_1_col
				}, {
					$value: "2",
					$title: this.designer.localize.fchoice_layout_2_col
				}, {
					$value: "3",
					$title: this.designer.localize.fchoice_layout_3_col
				}, {
					$value: "4",
					$title: this.designer.localize.fchoice_layout_4_col
				}]
			}
		};
	},
	getSection: function() {
		return {
			$category: "section",
			$authoringType: "choiceField",
			$authoringEditMode: true,
			$isTitleHidden: true,
			$layout: {
				$items: [{
					$bind: "$choiceField$format",
					$isEditMode: true,
					$css: "s-aw-prop",
					$choiceLayout: "1"
				}, {
					$bind: "$choiceField$choiceLayout",
					$format: "$combo",
					$isEditMode: true,
					$css: "s-aw-prop"
				}]
			}
		};
	},
	onShow: function() {
		if (this.designer.awItem.$authoringType == "choiceField") {
			this.designer.applyChange({
				$choiceField$format: this.designer.awItem.$item.$format,
				$choiceField$choiceLayout: this.designer.awItem.$item.$choiceLayout
			});
		}
	},
	notifyDataChange: function(metaData) {
		if (this.designer.awItem.$authoringType == "choiceField") {
			if (metaData.$choiceField$format != undefined || metaData.$choiceField$choiceLayout != undefined) {
				this.designer.awItem.applyDesignMetaData({
					$format: metaData.$choiceField$format,
					$choiceLayout: metaData.$choiceField$choiceLayout
				}, true);
			}
		}
	},
	onDesignerApplyChange: function(newData) {

	},
	dispose: function() {
		this.designer = null;

	}
});