"use strict";
var helpers = require('syracuse-core/lib/helpers');
var PageDesigner = require("syracuse-ui/lib/authoring/pageDesigner").Designer;
var Designer = require("syracuse-ui/lib/authoring/designer").Designer;

var _style = (function() {
	var map = {
		xy: "line",
		point: "scatter",
		stick: "column",
		radar: "spiderweb"
	};

	return function($style) {
		return $style && (map[$style] || $style);
	};
})();


function _loadGraphDesigner(designer) {
	var list = designer.designedArticle;
	var hasDecorator = !! list.$prototype.$decorator;
	var hasCube = !! list.$prototype.$cube;
	if (hasDecorator || hasCube) {
		designer.graph = {
			styleFields: {},
			colorFields: {},
			defaultStyle: "column",
			defaultColor: "#409f8b",
			styles: ["area", "areaspline", "column", "line", "scatter", "spline", "pie", "bar"],
			colors: [{
				$value: "#007f64",
				$title: "green"
			}, {
				$value: "#34b233",
				$title: "brightgreen"
			}, {
				$value: "#4d4f53",
				$title: "darkgrey"
			}, {
				$value: "#9a9b9c",
				$title: "midgray"
			}, {
				$value: "#ff5800",
				$title: "orange"
			}, {
				$value: "#009fda",
				$title: "blue"
			}, {
				$value: "#6639b7",
				$title: "purple"
			}, {
				$value: "#409f8b",
				$title: "1"
			}, {
				$value: "#67c566",
				$title: "2"
			}, {
				$value: "#7a7b7e",
				$title: "3"
			}, {
				$value: "#b3b4b5",
				$title: "4"
			}, {
				$value: "#ff8240",
				$title: "5"
			}, {
				$value: "#40b7e3",
				$title: "6"
			}, {
				$value: "#8c6bc9",
				$title: "7"
			}]
		};
		var table = document.createElement("div");
		table.className = "s-aw-graph";
		var slot = document.createElement("div");
		slot.className = "s-aw-graph-pos";
		var title = document.createElement("div");
		title.className = "s-aw-graph-title";
		title.textContent = syra_local.lw_graphPosition;
		slot.appendChild(title);
		designer.loadNewItem(slot, {
			$bind: "$graphPosition",
			$isTitleHidden: true,
			$contentEditable: true,
			$isTopLabelAlignment: false,
			$css: "s-aw-prop",
			$isEditMode: true,
			$format: "$combo"
		});
		table.appendChild(slot);

		designer.settingsSlot.appendChild(table);
		if (hasCube) {
			slot = document.createElement("div");
			slot.className = "s-aw-graph-measures";
			table.appendChild(slot);
			/*title = document.createElement("div");
             title.className = "s-aw-graph-title";
             title.textContent = syra_local.lw_graphAxis;
             slot.appendChild(title);*/
			var measures = document.createElement("div");
			measures.className = "s-aw-graph-measures-list";
			var delta = {};
			var binds = Object.keys(list.$prototype.$cube.$measures);
			for (var ii = 0, jj = binds.length; ii < jj; ii++) {
				designer.appendMeasure(binds[ii], delta, measures);
			}
			slot.appendChild(measures);
			designer.applyChange(delta);
		}
	}
}


var CardDesigner = helpers.defineClass(function() {}, PageDesigner, {
	drawBox: function() {
		PageDesigner.prototype.drawBox.call(this);
		syra_site.dom.removeChild(this.viewsSlot);
		this.viewsSlot = null;
		this.cardTitle = document.createElement("div");
		this.cardTitle.className = "s-aw-carddesigner-title";
		var title = this.designedArticle.list.getTitle() || "";
		if (title.length) {
			title += " - ";
		}
		title += this.designedArticle.list.$item.$format == "grid" ? syra_local.aw_card_detail_title : syra_local.aw_card_card_title;
		this.cardTitle.textContent = title;
		this.leftTop.className += " s-aw-carddesigner-left-top";
		this.layoutSlot.insertBefore(this.cardTitle, this.leftTop.nextSibling);
		this._togleOverLaySlots(true);
	},
	_togleOverLaySlots: function(show) {
		var ids = ["left", "top", "right", "bottom"];
		if (show) {
			this.overlays = {};
			for (var ii = 0, jj = ids.length; ii < jj; ii++) {
				var overlay = this.overlays[ids[ii]] = document.createElement("div");
				overlay.syraIsOverlay = true;
				overlay.className = "s-aw-carddesigner-overlay";
				this.designedArticle.page.scrollview.appendChild(overlay);
			}
		} else {
			if (this.overlays) {
				for (var ii = 0, jj = ids.length; ii < jj; ii++) {
					syra_site.dom.removeChild(this.overlays[ids[ii]]);
				}
				this.overlays = null;
			}
		}
	},
	endArticleUpdate: function(selectedItem, isStructureUpdated) {
		PageDesigner.prototype.endArticleUpdate.call(this, selectedItem, isStructureUpdated);
		this.resizeArticle();
	},
	resizeArticle: function(resize) {
		PageDesigner.prototype.resizeArticle.call(this, resize);
		if (this.overlays && this.designedArticle.domItem) {
			var rectHeader = this.designedArticle.page.header.getBoundingClientRect();
			var rect = this.designedArticle.page.body.getBoundingClientRect();
			var rectCard = this.designedArticle.domItem.getBoundingClientRect();

			var top = rectHeader.height + (rectCard.top - rect.top);
			this.overlays.left.style.top = top + "px";
			this.overlays.left.style.left = 0;
			this.overlays.left.style.width = (rectCard.left - rect.left) + "px";
			this.overlays.left.style.height = rectCard.height + "px";

			this.overlays.right.style.top = top + "px";
			this.overlays.right.style.right = 0;
			this.overlays.right.style.width = (rect.right - rectCard.right) + "px";
			this.overlays.right.style.height = rectCard.height + "px";

			this.overlays.top.style.top = 0;
			this.overlays.top.style.left = 0;
			this.overlays.top.style.width = rect.width + "px";
			this.overlays.top.style.height = top + "px";

			this.overlays.bottom.style.top = (top + rectCard.height) + "px";
			this.overlays.bottom.style.left = 0;
			this.overlays.bottom.style.width = rect.width + "px";
			this.overlays.bottom.style.height = (rect.bottom - rectCard.bottom) + "px";
		}
	},
	openDesigner: function(cardItem) {
		cardItem.list.designer.cardDesigner = cardItem.designer = this;
		cardItem.$designLevel = "article";
		PageDesigner.prototype.openDesigner.call(this, cardItem);
	},
	closeDesigner: function() {
		if (this.history.currentStep > 1) {
			this.designedArticle.list.designer.history.updateSteps(true);
		}
		this.dispose();
		syra_site.resize();
	},
	dispose: function() {
		var cardItem = this.designedArticle;
		this._togleOverLaySlots(false);
		PageDesigner.prototype.dispose.call(this);
		if (cardItem) {
			if (cardItem.list && cardItem.list.designer) {
				cardItem.list.designer.cardDesigner = null;
			}
			cardItem.designer = null;
		}
	}
});


exports.Designer = helpers.defineClass(function() {}, Designer, {
	closeDesigner: function() {
		syra_site.switchItemDesigner(this.designedArticle, false);
	},
	loadBox: function(designedPage) {
		this.$prototype = {
			$properties: {
				$cardPosition: {
					$type: "application/x-choice",
					$value: {
						$type: "application/x-string",
						$enum: [{
							$value: "row",
							$title: syra_local.lw_cardPositionRow
						}, {
							$value: "top",
							$title: syra_local.lw_cardPositionTop
						}, {
							$value: "left",
							$title: syra_local.lw_cardPositionLeft
						}, {
							$value: "right",
							$title: syra_local.lw_cardPositionRight
						}, {
							$value: "bottom",
							$title: syra_local.lw_cardPositionBottom
						}]
					}
				},
				$graphPosition: {
					$type: "application/x-choice",
					$title: syra_local.lw_graphPosition,
					$value: {
						$type: "application/x-string",

						$enum: [{
							$value: "top",
							$title: syra_local.lw_graphPositionTop
						}, {
							$value: "left",
							$title: syra_local.lw_graphPositionLeft
						}, {
							$value: "right",
							$title: syra_local.lw_graphPositionRight
						}, {
							$value: "bottom",
							$title: syra_local.lw_graphPositionBottom
						}, {
							$value: "front",
							$title: syra_local.lw_graphPositionFront
						}, {
							$value: "behind",
							$title: syra_local.lw_graphPositionBehind
						}]
					}
				}
			}
		};
		this.$item = {
			$layout: {
				$items: []
			}
		};
		Designer.prototype.loadBox.call(this);
		this.applyListDesignMetaData();
	},
	drawBox: function() {
		this.domItem = this.body = this.layoutSlot = document.createElement("aside");
		this.domItem.syraItem = this.domItem.syraarticle = this.id;
		this.layoutSlot.className = "s-aw-list";
		this.designedArticle._core.parentNode.insertBefore(this.layoutSlot, this.designedArticle._core);
		this.layoutSlot.style.display = "";
		this.layoutSlot.appendChild(this.addCloseButton());
		this.settingsSlot = document.createElement("div");
		this.settingsSlot.className = "s-aw-list-body";
		this.layoutSlot.appendChild(this.settingsSlot);

		this.designedItem = this.designedArticle;
		this.loadHistoryTool();
		this.loadTemplateTools();

		this.appendGroupModes();

		_loadGraphDesigner(this);
		this.getDesignedCardItem(); //ensuser one carditem is opened
		syra_site.resize();
	},
	endArticleUpdate: function() {
		var record = this.getDesignedCardItem();
		if (record && record.layoutContent) {
			syra_site.ensureArticleVisibility(this.designedArticle.page);
		}
		this.history.updateSteps();
		if (this.designedArticle.page.designer) {
			this.designedArticle.page.designer.endArticleUpdate(this.designedArticle);
		} else {
			this.designedArticle.resizeArticle(true);
			this.saveDesign();
		}
	},
	saveDesign: function() {
		if (this.designedArticle.page.designer) {
			this.designedArticle.page.designer.saveDesign();
		} else {
			this.designedArticle.page.savePageDesign();
		}

	},
	applyListDesignMetaData: function() {
		var delta = {
			$format: this.designedArticle.$item.$format || "grid",
			$graphPosition: this.designedArticle.$item.$graphPosition || "bottom"
		};
		if (this.designedArticle.$item.$cardItem) {
			delta.$cardPosition = this.designedArticle.$item.$cardItem.$position || "bottom";
		} else {
			delta.$cardPosition = "no";
		}
		this.applyChange(delta);
	},
	onSwitchPagerClick: function(event, btn) {
		this.designedArticle.paging.onPageClick(event, btn);
		this._setSwitchPagerBtnsTitle();
	},
	onDesignCardClick: function() {
		var designer = new CardDesigner();
		designer.openDesigner(this.getDesignedCardItem());
		designer.resizeArticle();
	},
	onModeClick: function(event, btn) {
		var designer = this;
		var metaData = {};
		switch (btn.syraMode) {
			case "cards":
				metaData.$format = "cards";
				break;
			case "grid":
				metaData.$format = "grid";
				metaData.$cardPosition = "no";
				break;
			case "detailgrid":
				metaData.$format = "grid";
				metaData.$cardPosition = this.dataset.$cardPosition || "no";
				if (metaData.$cardPosition == "no") {
					var $designing = this.designedArticle.$designing;
					if ($designing && $designing.grid && $designing.grid.$cardItem) {
						metaData.$cardPosition = $designing.grid.$cardItem.$position || "no";
					}
					if (metaData.$cardPosition == "no") {
						metaData.$cardPosition = "bottom";
					}
				}
				break;
		}
		var list = designer.designedArticle;
		list.$designing = list.$designing || {};
		if (list.$item.$format == "grid") {
			list.$designing.grid = list.$designing.grid || {};
			list.$designing.grid.$layout = helpers.object.clone(list.$item.$layout, true);
			if (list.$item.$cardItem) {
				list.$designing.grid.$cardItem = helpers.object.clone(list.$item.$cardItem, true);
			}
		} else {
			list.$designing.cards = {
				$layout: helpers.object.clone(list.$item.$layout, true)
			};
		}
		if (metaData.$format !== undefined && metaData.$format != list.$item.$format) {
			delete list.$item.$cardItem;
			delete list.$item.$layout;
			list.$item.$format = metaData.$format;
			list.reloadLayout();
			designer = list.designer;
		}

		designer.designedArticle.applyDesignMetaData(metaData, true);
		designer.endArticleUpdate();
		designer.applyChange(metaData);
	},

	getDesignedCardItem: function() {
		var cardItem, record = this.designedArticle.records[0];
		if (this.designedArticle.$item.$format == "grid") {
			if (this.designedArticle.$item.$cardItem) {
				if (this.designedArticle.builder.toggleRowCard) {
					this.designedArticle.builder.toggleRowCard(record, true);
					cardItem = record;
				} else {
					for (var ii = 0, jj = this.designedArticle.records.length; ii < jj; ii++) {
						record = this.designedArticle.records[ii];
						if (record.cardItem) {
							cardItem = record.cardItem;
							break;
						}
					}
				}
			}
		} else {
			cardItem = record.cardItem;
		}
		return cardItem;
	},
	_addSwitchPagerBtn: function(slot) {
		if (this.designedArticle.isClientFetch) {
			var btn = syra_menus.addTextButton("", "s-aw-list-pager-btn", "onSwitchPagerClick");
			btn.syraIsDesign = true;
			slot.appendChild(btn);
			(this._switchPagerBtns = this._switchPagerBtns || []).push(btn);
		}
	},
	_setSwitchPagerBtnsTitle: function() {
		if (this._switchPagerBtns) {
			var title = this.designedArticle.$item.$isPagerHidden ? syra_local.lw_showPager : syra_local.lw_hidePager;
			for (var ii = 0, jj = this._switchPagerBtns.length; ii < jj; ii++) {
				syra_menus.setButtonTitle(this._switchPagerBtns[ii], title);
			}
			this.designedArticle.paging.draw(true);
		}
	},
	appendGroupModes: function() {
		this.modes = {};
		var table = document.createElement("div");
		table.className = "s-aw-list-slot";
		if (!this.designedArticle.page.isVignettePage && !this.designedArticle.page.designer) {
			table.style.display = "none";
		}
		var title = document.createElement("div");
		title.className = "s-aw-list-title";
		title.textContent = syra_local.lw_presentation;
		var slot = document.createElement("div");
		slot.className = "s-aw-list-modes";
		var modesBody = document.createElement("div");
		modesBody.className = "s-aw-list-modes-body";
		slot.appendChild(title);
		slot.appendChild(modesBody);
		table.appendChild(slot);

		var modeTypes = ["cards", "grid", "detailgrid"];
		for (var ii = 0, jj = modeTypes.length; ii < jj; ii++) {
			var modeType = modeTypes[ii];
			var mode = {
				modeType: modeType,
				link: syra_menus.addIconButton(syra_local["lw_mode_" + modeType], "s-aw-list-mode-" + modeType, "onModeClick"),
				box: document.createElement("div")
			};
			mode.box.className = "s-aw-list-mode";
			var title = document.createElement("div");
			title.className = "s-aw-list-title";
			title.textContent = syra_local["lw_mode_" + modeType + "_title"];
			mode.box.appendChild(title);
			var body = document.createElement("div");
			body.className = "s-aw-list-mode-body";
			mode.box.appendChild(body);
			mode.link.syraMode = modeType;
			modesBody.appendChild(mode.link);
			this.modes[modeType] = mode;
			table.appendChild(mode.box);
			switch (modeType) {
				case "grid":
					this._addSwitchPagerBtn(body);
					break;
				case "cards":
					var slot = document.createElement("div");
					slot.className = "s-aw-list-card-position";
					this.sectionCards = {
						body: body.appendChild(slot)
					};
					this.addColsCountButtons(this.sectionCards, this.designedItem, this.designedArticle.builder.$cardsByRowCount || 1, 6);
					body.appendChild(syra_menus.addTextButton(syra_local.lw_designCard, "s-aw-list-design-card-picker", "onDesignCardClick"));
					this._addSwitchPagerBtn(body);
					break;
				case "detailgrid":
					var slot = document.createElement("div");
					slot.className = "s-aw-list-card-position";
					this.loadNewItem(body.appendChild(slot), {
						$bind: "$cardPosition",
						$choiceLayout: "row",
						$isTopLabelAlignment: false,
						$format: "$button",
						$icon: {
							$path: "authoring/s-aw-grid-card-"
						},
						$isEditMode: true,
						$css: "s-aw-prop"
					});
					body.appendChild(syra_menus.addTextButton(syra_local.lw_designDetailView, "s-aw-list-design-card-picker", "onDesignCardClick"));
					this._addSwitchPagerBtn(body);
					break;
			}
		}
		this._setSwitchPagerBtnsTitle();
		this.settingsSlot.appendChild(table);
	},
	applyChange: function(newData) {
		if (newData) {
			newData.$properties = newData.$properties || {};
			if (newData.$format) {
				var isSelectedBoxeHidden;
				var delta = newData.$properties = newData.$properties || {};
				var selectedMode = newData.$format;
				if (newData.$format == "grid") {
					if (newData.$cardPosition != "no") {
						selectedMode = "detailgrid";
					} else {
						isSelectedBoxeHidden = !this._switchPagerBtns;
					}
				}
				var types = Object.keys(this.modes);
				for (var ii = 0, jj = types.length; ii < jj; ii++) {
					var mode = this.modes[types[ii]];
					var isSelected = types[ii] == selectedMode;
					syra_site.dom.toggleClass(mode.link, "s-selected", isSelected);
					mode.box.style.display = (isSelected && !isSelectedBoxeHidden) ? "" : "none";
				}
			}
			if (this.graph) {
				var keys = Object.keys(newData);
				for (var ii = 0, jj = keys.length; ii < jj; ii++) {
					var $bind = keys[ii];
					if (this.graph.styleFields[$bind]) {
						this.graph.colorFields[$bind].domItem.style.visibility = newData[$bind] == "pie" ? "hidden" : "";
					}
				}
			}
		}
		Designer.prototype.applyChange.call(this, newData);
	},
	onNotifyDataChange: function(field, value) {
		var metaData = {};
		var $bind = field.$item.$bind;
		metaData[$bind] = value;
		if (field.$item.$measure) {
			var $cube = this.designedArticle.$item.$cube || {};
			$cube.$measures = $cube.$measures || {};
			($cube.$measures[field.$item.$measure] = $cube.$measures[field.$item.$measure] || {})[field.$item.$axisOption] = value;
			this.designedArticle.applyDesignMetaData({
				$cube: $cube
			}, true);
		} else {
			if (metaData.$cardPosition !== undefined) {
				if (!(metaData.$cardPosition == "no" || metaData.$cardPosition == "row")) {
					if (this.designedArticle.$item.$graphPosition == "front" || this.designedArticle.$item.$graphPosition == "behind") {
						metaData.$graphPosition = "bottom";
					}
				}
			}
			this.designedArticle.applyDesignMetaData(metaData, true);
			var record = this.getDesignedCardItem();
			if (record && record.designer) {
				record.designer.toggleUIDesign(record, true);
			}
		}
		this.endArticleUpdate();
		this.applyChange(metaData);
		return false;
	},
	onEndHistoryChangeStep: function() {
		this.applyListDesignMetaData();
	},
	resizeArticle: function() {
		this.cardDesigner && this.cardDesigner.resizeArticle();
	},

	appendMeasure: function(bind, delta, body) {
		var chart = this.designedArticle.chart;
		var $itemMeasures = (this.designedArticle.$item.$cube && this.designedArticle.$item.$cube.$measures) || {};
		//var $measure = this.designedArticle.$prototype.$cube.$measures[bind];
		var serie = chart._meta.series[bind];

		var slot = document.createElement("div");
		slot.className = "s-aw-graph-meassure";
		var title = document.createElement("div");
		title.textContent = serie.measure.$title ? this.designedArticle.page.getLocalizeText(serie.measure.$title) : "";
		title.className = "s-aw-graph-meassure-title";
		slot.appendChild(title);

		var $enum = [];
		for (var mm = 0, kk = this.graph.styles.length; mm < kk; mm++) {
			$enum.push({
				$value: this.graph.styles[mm],
				$title: syra_local[this.graph.styles[mm]] || this.graph.styles[mm]
			});
		}
		this.$prototype.$properties["$cube" + bind] = {
			$type: "application/x-choice",
			$value: {
				$type: "application/x-string",
				$enum: $enum
			}
		};

		this.$prototype.$properties["$cube-color-" + bind] = {
			$type: "application/x-choice",
			$value: {
				$type: "application/x-string",
				$enum: helpers.object.clone(this.graph.colors, true)
			}
		};

		var cell = document.createElement("div");
		cell.className = "s-aw-graph-meassure-item";
		slot.appendChild(cell);
		this.graph.styleFields["$cube" + bind] = this.loadNewItem(cell, {
			$category: "field",
			$isTitleHidden: true,
			$bind: "$cube" + bind,
			$measure: bind,
			$axisOption: "$style",
			$isTopLabelAlignment: false,
			$icon: {
				$mode: "icon",
				$path: "site/s-graph-"
			},
			$isEditMode: true,
			$css: "s-aw-prop"
		});
		var $style, $color;
		if ($itemMeasures[bind]) {
			$style = $itemMeasures[bind].$style;
			$color = $itemMeasures[bind].$color;
		}
		delta["$cube" + bind] = _style($style || serie.measure.$style || this.graph.defaultStyle);
		delta["$cube-color-" + bind] = $color || serie.measure.$color || this.graph.defaultColor;

		var cell = document.createElement("div");
		cell.className = "s-aw-graph-meassure-item";
		slot.appendChild(cell);
		this.graph.colorFields["$cube" + bind] = this.loadNewItem(cell, {
			$category: "field",
			$isTitleHidden: true,
			$bind: "$cube-color-" + bind,
			$measure: bind,
			$axisOption: "$color",
			$isTopLabelAlignment: false,
			$colorChoice: true,
			$isEditMode: true
		});
		body.appendChild(slot);
	},
	dispose: function() {
		var paging = this.designedArticle && this.designedArticle.paging;
		syra_site.dom.removeChild(this.layoutSlot);
		Designer.prototype.dispose.call(this);
		paging && paging.draw();
	}
});