"use strict";
function getLayout($level){
    return {
        "$category": "section",
        "$skin": "s-aw-h2",
        "$css": "s-aw-palette-" + $level,
        "$isBoxCollapsable": true,
        "$title": "{@layout}",
        "$layout": {
            "$items": [{
                $category: "menus",
                $title: "{@addrow}",
                $skin: "s-aw-cols",
                $isHidden: $level != "article",
                "$itemSkin": "s-aw-cols-link",
                "$layout": {
                    "$layoutType": "stack",
                    "$items": [{
						"$css" : "s-aw-cols-add-before",
                        "$bind": "$addRowBefore"
                    }, {
						"$css" : "s-aw-cols-add-after",
                        "$bind": "$addRowAfter"
                    }]
                }
            }, {
                $category: "menus",
                $title: "{@columns}",
                $skin: "s-aw-cols",
                "$itemSkin": "s-aw-cols-link",
                "$layout": {
                    "$layoutType": "row",
                    "$fitToContent": true,
                    "$items": [{
                        "$bind": "s-layout-type-1",
                    }, {
                        "$bind": "s-layout-type-2"
                    }, {
                        "$bind": "s-layout-type-3"
                    }, {
                        "$bind": "s-layout-type-4"
                    }, {
                        "$bind": "s-layout-type-5"
                    }]
                }
            }, {
                "$category": "field",
                "$isHidden": !($level == "article" || $level == "section"),
                "$css": "s-layout-tab-choice",
                "$bind": "$layoutTabChoice",
                "$skin": "s-aw-field",
                "$isEditMode": true,
                "$format": "$button",
                "$choiceLayout": "row",
                "$icon": {
                    "$mode": "icon",
                    "$path": "authoring/s-layout-"
                }
            }]
        }
    };
}

