"use strict";
function getRow(isField){
    return {
        "$category": "section",
        "$skin": "s-aw-h2",
        "$css": "s-aw-palette-layout",
        "$isBoxCollapsable": true,
        "$title": "{@rowLayout}",
        "$layout": {
            "$items": [{
                $category: "menus",
                $title: "{@addrow}",
                $skin: "s-aw-cols",
                $isHidden: true,
                "$itemSkin": "s-aw-cols-link",
                "$layout": {
                    "$layoutType": "stack",
                    "$items": [{
                        "$css": "s-aw-cols-add-before",
                        "$bind": "$addRowBefore"
                    }, {
                        "$css": "s-aw-cols-add-after",
                        "$bind": "$addRowAfter"
                    }]
                }
            }, {
                $category: "menus",
                $title: "{@columns}",
                $skin: "s-aw-cols",
                "$itemSkin": "s-aw-cols-link",
                "$layout": {
                    "$layoutType": "row",
                    "$fitToContent": true,
                    "$items": [{
                        "$bind": "s-layout-type-1"
                    }, {
                        "$bind": "s-layout-type-2"
                    }, {
                        "$bind": "s-layout-type-3"
                    }, {
                        "$bind": "s-layout-type-4"
                    }, {
                        "$bind": "s-layout-type-5"
                    }, {
                        "$bind": "s-layout-type-6"
                    }]
                }
            }]
        }
    };
}

function getLayout($level){
    var $layout = {
        "$category": "section",
        "$skin": "s-aw-h2",
        "$css": "s-aw-palette-" + $level,
        "$isBoxCollapsable": true,
        "$title": "{@layout}",
        "$layout": {
            "$items": [{
                $category: "menus",
                $title: "{@columns}",
                $skin: "s-aw-cols",
                "$itemSkin": "s-aw-cols-link",
                "$layout": {
                    "$layoutType": "row",
                    "$fitToContent": true,
                    "$items": [{
                        "$bind": "s-layout-type-1",
                        "$css": $level == "field" ? "" : "s-aw-row-box"
                    }, {
                        "$bind": "s-layout-type-2",
                        "$css": $level == "field" ? "" : "s-aw-row-box"
                    }, {
                        "$bind": "s-layout-type-3",
                        "$css": $level == "field" ? "" : "s-aw-row-box"
                    }, {
                        "$bind": "s-layout-type-4",
                        "$css": $level == "field" ? "" : "s-aw-row-box"
                    }, {
                        "$bind": "s-layout-type-5",
                        "$css": $level == "field" ? "" : "s-aw-row-box"
                    }, {
                        "$bind": "s-layout-type-6",
                        "$css": $level == "field" ? "" : "s-aw-row-box"
                    }]
                }
            }, {
                "$category": "field",
                "$isHidden": !($level == "article" || $level == "section"),
                "$css": "s-layout-tab-choice",
                "$bind": "$layoutTabChoice",
                "$skin": "s-aw-field",
                "$isEditMode": true,
                "$format": "$button",
                "$choiceLayout": "row",
                "$icon": {
                    "$mode": "icon",
                    "$path": "authoring/s-layout-"
                }
            }]
        }
    };
    if ($level == "article") {
        $layout.$layout.$items.push({
            $title: "{@models}",
            $id: "s-awModels",
            $isHidden: false,
            "$category": "menus",
            "$itemIcon": {
                "$mode": "icon",
                "$path": "authoring/s-aw-"
            },
            "$skin": "s-aw-menus",
            "$itemSkin": "s-aw-menus-link",
            "$isBoxCollapsable": "true",
            "$opened": false,
            "$layout": {
                "$items": [{
                    "$layoutType": "row",
                    "$items": [{
                        "$icon": null,
                        "$bind": "pattern-headerTabs"
                    }, {
                        "$icon": null,
                        "$bind": "$defaultLayout"
                    }]
                }, {
                    "$layoutType": "row",
                    "$items": [{
                        "$bind": "pattern-1"
                    }, {
                        "$bind": "pattern-2"
                    }, {
                        "$bind": "pattern-3"
                    }, {
                        "$bind": "pattern-4"
                    }]
                }]
            }
        });
    }
    return $layout;
}

