"use strict";

exports.getRecords = function(count){
    var records = [];
    for (var index = 0; index < count; index++) {
        records.push({
            "$uuid": "df3f7fa4-7b64-4daa-b1f0-2222" + index,
            "$etag": 1,
            "$": {},
            "$index": index,
            "application/x-string": "record " + index,
            "application/x-choice": index,
            "application/x-date": "2011-0" + Math.max(Math.min(1, index), 9) + "-07",
            "application/x-boolean": true,
            "application/x-decimal": 45454.0 * index
        });
    }
    return records;
};


