"use strict";
var helpers = require('syracuse-core/lib/helpers');
var Box = require('syracuse-ui/lib/common/article/box').Box;

var _localize = {
    sectionTitle: "Section",
    blockTitle: "block"
};
function DesktopSection(){

}

exports.DesktopSection = helpers.defineClass(DesktopSection, Box, {
    getDefaultTitle: function(){
        return _localize.sectionTitle;
    },
    loadBox: function(){
        this.$htmlTag = "section";
        this._ensureSkin(this.$authoringLevel = "section");
        Box.prototype.loadBox.call(this);
    }
});

function DesktopBlock(){

}

exports.DesktopBlock = helpers.defineClass(DesktopBlock, Box, {
    getDefaultTitle: function(){
        return _localize.blockTitle;
    },
    loadBox: function(){
        this._ensureSkin(this.$authoringLevel = "block");
        Box.prototype.loadBox.call(this);
    }
});
