"use strict";
var helpers = require('syracuse-core/lib/helpers');
var Box = require('syracuse-ui/lib/common/article/box').Box;

function DesktopSection(){

}

exports.DesktopSection = helpers.defineClass(DesktopSection, Box, {
    loadBox: function(){
        this.$htmlTag = "section";
        this._ensureSkin(this.$authoringLevel = "section");
        Box.prototype.loadBox.call(this);
    }
});

