"use strict";
var helpers = require('syracuse-core/lib/helpers');
var Article = require("syracuse-ui/lib/common/article/article").Article;


function CardRecord(){
}

exports.CardRecord = helpers.defineClass(CardRecord, Article, {
    loadRecord: function(list, record, $recordIndex, row, cellIndex){
        this.$recordUuid = record.$uuid;
        this.$recordIndex = $recordIndex;
        this.$$recordContainer = row.$$row;
        this.$facet = list.$recordFacet;
        this.$prototype = list.$prototype.$item;
        this.row = row;
        this.cellIndex = cellIndex;
        document.itemFactory.initializeItem(this, {
            $category: "section",
            $layout: {
                $items: list.$item.$layout.$items
            }
        }, list);
        this.loadBox(record);
    },
    remove: function(){
        this.$$item.remove();
    },
    drawBox: function(){
        var list = this.boxParent;
        this.$$item = this.row.$$cells[this.cellIndex].attr("data-s-record", this.$recordUuid);
        if (list.$alternateStyle && this.$recordIndex % 2) {
            this.$$item.addClass(list.$alternateSkin);
        }
        var selectorCell = document.createElement("div");
        var $$selectorCell = $(selectorCell).appendTo(this.$$item);
        
        var dom = document.createElement("div");
        dom.className = list.$skin + "-record";
        var $$data = $(dom).appendTo(this.$$item);
        if (!list.$item.$isMenuRecordHidden) {
            dom = document.createElement("div");
            dom.className = list.$skin + "-menu-row";
            this.$$menuContainer = $(dom).appendTo(this.$$item);
        }
        if (list.recordSelector.hasColumn) {
            selectorCell.className = list.$skin + list.recordSelector.css;
            $$selectorCell.append(this.$$selector = $(list.recordSelector.record));
        }
        else {
            selectorCell.style.display = "none";
        }
        document.itemFactory.load($$data, list.builder.$cardview, this);
        this.$$item[0].style.visibility = "";
    },
    highlightSelection: function(selected){
        document.site.toggleClass(this.$$item, "s-list-record-selected", selected);
        if (this.$$selector && this.$$selector.is("input")) {
            this.$$selector.attr('checked', selected);
        }
    },
    dispose: function(){
        delete this.row;
        delete this.menus;
        Article.prototype.dispose.call(this);
    }
});
