"use strict";
var helpers = require('syracuse-core/lib/helpers');
var HGridRecord = require('./hgridRecord').HGridRecord;

function VGridRecord(){
}

exports.VGridRecord = helpers.defineClass(VGridRecord, HGridRecord, {
    drawBox: function(){
        var self = this;
        var css = self.$skin + "-cell";
        if (self.list.recordSelector.hasColumn) {
            self.builder.$$VGridRows[self.builder.$rowSelector].append($("<td/>").addClass(css).addClass(self.list.$skin + self.list.recordSelector.css).append(self.$$selector = $(self.list.recordSelector.record)));
            self.$$selector.attr("data-s-record", self.$recordUuid);
        }
        var htmlCell = "<td class='" + self.list.$skin + "-cell'/>";
        self.list.parseItems(function($item, $field){
            var $$htmlCell = $(htmlCell);
            if ($field.$isHidden !== undefined) {
                $$htmlCell.toggle(!$field.$isHidden);
            }
            document.itemFactory.load($$htmlCell.appendTo(self.builder.$$VGridRows[$item.$bind]), {
                $bind: $item.$bind,
                $isCellChild: true,
                $inplace: true
            }, self);
        });
        if (!self.list.$item.$isMenuRecordHidden) {
            self.$$menuContainer = $(htmlCell).appendTo(self.builder.$$VGridRows[self.builder.$rowMenu]);
            self.list.toggleRecordMenu(true, self);
        }
    },
    remove: function(){
    
    },
    highlightSelection: function(selected){
        if (this.$$selector && this.$$selector.is("input")) {
            this.$$selector.attr('checked', selected);
        }
    }
});
