"use strict";
exports.load = function(field){
    if (field.$field.$location) {
        document.controller.loadRepresentation(null, field.$field.$location.$url, function($itemPage){
            $itemPage.$$container = field.$$body;
            $itemPage.$category = "portlet";
            $itemPage.$skin = $itemPage.$skin || "s-portlet-widget-menus";
            field.portlet = document.itemFactory.loadPage($itemPage);
        });
    }
    else {
        field.portlet = document.itemFactory.loadPage({
            $$container: field.$$body,
            $representation: {
                $prototype: {
                    $links: field.$field.$links,
                    $actions: field.$field.$actions
                },
                $article: {
                    $category: "portlet",
                    $layout: {
                        $items: [{
                            $skin: "s-portlet-widget-menus",
                            $columnLimit: field.$item.$columnLimit,
                            $moreLimit: field.$item.$moreLimit,
                            $category: "links",
                            $menus: field.$field.$menus
                        }]
                    }
                }
            },
            $urlParts: {
                $facet: "$menu"
            },
            $category: "portlet"
        });
    }
};
exports.getAuthoringSettings = function(field){
    return null;
};
exports.dispose = function(){
};
