exports.Template = function(template, syntax){

    var _syntax = syntax || /(^|.|\r|\n)(\{\s*(\w+)\s*})/; //Should improve this regexp
    var _template = template;
    var self = this;
    
    function isProperty(str){
        return (/^\{/.test(str) && /\}$/.test(str));
    }
    
    self.evaluate = function(object){
        var tokens = _template.split(_syntax);
        var result = [];
        for (var i = 0; i < tokens.length; i++) {
            var token = tokens[i];
            if (isProperty(token)) {
                i++;
                token = tokens[i];
                if (object.hasOwnProperty(token)) {
                    result.push(object[token]);
                }
            }
            else {
                result.push(token);
            }
        }
        return result.join('');
    };
    
    return self;
}
