"use strict";
var helpers = require('syracuse-core/lib/helpers');
var locale = require('syracuse-core/lib/locale');
var AreaScroller = require('syracuse-ui/lib/article/areaScroller').AreaScroller;
var DiagnoseGroup = require('syracuse-ui/lib/diagnoses/diagnoseGroup').DiagnoseGroup;

function DiagnosesPanel(){
}

exports.DiagnosesPanel = helpers.defineClass(DiagnosesPanel, null, {
    _emptyViewer: function(){
        this._fieldDiagnoses = null;
        this.$$item.hide();
        if (this._menus) {
            this._menus.unload();
            delete this._menus;
        }
        this._emptyGroups();
        this._fitPageToContainer();
    },
    _emptyGroups: function(field){
        if (this._groups) {
            for (var ii = 0, jj = this._groups.length; ii < jj; ii++) {
                this._groups[ii].empty(field);
            }
        }
    },
    appendToPage: function(page){
        this.page = page;
        var html = document.createElement("div");
        html.className = "s-diag-view";
        this.$$item = $(html).appendTo($(this.page.diagnoseSlot));
    },
    _refreshViewer: function(options, page){
        this.total = 0;
        this.$$body.empty();
        if (this._groups) {
            for (var ii = 0, jj = this._groups.length; ii < jj; ii++) {
                var group = this._groups[ii];
                var count = group.$$group.children().children("label").length;
                if (count) {
                    this.total += count;
                    group.$$group.appendTo(this.$$body);
                }
            }
        }
        // show diagnoses panel if any diagnose
        if (this.total > 0) {
            this.showDiagnosesPanel();
        }
        // 0 diagnose => clean
        else {
            this.$$item.hide();
        }
        this._fitPageToContainer();
    },
    _fitPageToContainer: function(){
        if (this.page.fitPageToContainer) {
            this.page.fitPageToContainer();
        }
    },
    showDiagnosesPanel: function(){
        // show next nav button if necessary (field diagnoses > 0)
        var fieldDiag = this.$$body.find("a.s-diag-view-msg-link");
        this._nextLink.style.display = (this._fieldDiagnoses.length > 1) ? "" : "none";
        
        // diagnoses in an array 
        var diagMsg = this.$$body.find("div.s-diag-view-msg");
        // show expand (more/less) button if necessary (total > 1)
        if (this.total > 1) { //(this.$$body.find("label").length > 1)
            // expand link text value for diagnose panel closed state
            var moreText = (this.total) - 1 == 0 ? this.localize.diag_expand : (this.total - 1) + this.localize.diag_expand;
            
            // if expand link already set
            if (this._expandLink.textContent) {
                // diagnoses panel is in opened mode
                if (this._expandLink.textContent.indexOf(this.localize.diag_collapse) != -1) {
                    for (var ii = 0; ii < diagMsg.length; ii++) {
                        diagMsg[ii].style.display = "block";
                    }
                }
                // diagnoses panel is in closed mode
                else {
                    for (var ii = 0; ii < diagMsg.length; ii++) {
                        diagMsg[ii].style.display = ii == 0 ? "block" : "none";
                    }
                    this._expandLink.textContent = moreText;
                }
            }
            // expand link not yet set
            else {
                for (var i = 0; i < diagMsg.length; i++) {
                    diagMsg[i].style.display = i == 0 ? "block" : "none";
                }
                this._expandLink.textContent = moreText;
            }
            this._expandLink.style.display = "";
        }
        else {
            // if one diag only, expand button is not necessary.
            if (this.total == 1) {
                diagMsg[0].style.display = "block";
                this._expandLink.textContent = "";
                this._expandLink.style.display = "none";
            }
        }
        
        // show diagnoses panel content
        this._core.style.display = "inline-block";
        this.$$item.show();
        document.site.setZIndex(this.$$item[0]);
        document.site.setZIndex(this._diagCloser);
        this._diagOpener.style.display = "none";
        
        if (this.onSuccess) {
            this._applySuccessStyle();
        }
    },
    _applySuccessStyle: function(){
        // append success menu icon
        // TODO
        /*
         menuItem.$$menuIcons.empty();
         var html = document.createElement("a");
         html.className = "s-page-menus-icons-info-success";
         menuItem.$$menuIcons[0].appendChild(html);
         */
        // apply success style to diagnoses panel
        this._core.className = "s-diag-view-core-success";
        this.$$core.find(".s-diag-view-links-body")[0].className = "s-diag-view-links-body-success";
        this.$$core.find(".s-diag-view-links")[0].className = "s-diag-view-links-success";
        this._diagViewLinks.className = "s-diag-view-links-bar-success";
        var listArray = this.$$diagViewLinks.find(".s-diag-view-links-link-layout-slot");
        var listArrayMain = this.$$diagViewLinks.find(".s-diag-view-links-link-layout-slot");
        var anchorArray = this.$$diagViewLinks.find("a.s-diag-view-links-link");
        var anchorArrayMain = this.$$diagViewLinks.find("a.s-diag-view-links-main-link");
        
        if (listArray[0]) {
            this._applyStyleToArray(listArray, "s-diag-view-links-link-layout-slot-success");
        }
        else 
            if (listArrayMain[0]) {
                this._applyStyleToArray(listArrayMain, "s-diag-view-links-link-layout-slot-success");
            }
        
        if (anchorArray[0]) {
            this._applyStyleToArray(anchorArray, "s-diag-view-links-link-success");
        }
        else 
            if (anchorArrayMain[0]) {
                this._applyStyleToArray(anchorArray, "s-diag-view-links-main-link-success");
            }
        this._fitPageToContainer();
    },
    _applyStyleToArray: function(array, css){
        for (var ii = 0; ii < array.length; ii++) {
            array[ii].className = css;
        }
    },
    _cleanSuccessStyle: function(){
        // clean diagnoses panel
        this._emptyViewer();
        // remove success style
        this._core.className = "s-diag-view-core";
        this._diagViewLinks.className = "s-diag-view-links-bar";
        // remove success icon from menu
        // TODO        
        this.onSuccess = false;
    },
    _initialize: function(){
        this.localize = locale.resources(module)();
        
        this._core = document.createElement("div");
        this._core.className = "s-diag-view-core";
        this.$$item.append(this.$$core = $(this._core));
        
        this._diagCloser = document.createElement("a");
        this._diagCloser.className = "s-diag-view-close";
        this._diagCloser.setAttribute("data-s-picker", "diag-close");
        this._core.appendChild(this._diagCloser);
        
        this._content = document.createElement("div");
        this._content.className = "s-diag-view-content";
        this._core.appendChild(this._content);
        
        var html = document.createElement("div");
        html.className = "s-diag-view-msg-list";
        this.$$body = $(html);
        
        this._groupsMap = {};
        this._groups = [];
        var severities = ["fatal", "error", "warning", "info", "success"];
        for (var ii = 0, jj = severities.length; ii < jj; ii++) {
            var severity = severities[ii];
            (this._groupsMap[severity] = new DiagnoseGroup()).create(severity, this);
            this._groups.push(this._groupsMap[severity]);
        }
        
        this._content.appendChild(this.$$body[0]);
        
        // navigation bar ("next" button) (navigation through diagnoses with field links)
        var diagNavBar = document.createElement("div");
        diagNavBar.className = "s-diag-view-navigation-bar";
        this._content.appendChild(diagNavBar);
        
        // navigation links/buttons ("next")
        this._nextLink = document.createElement("a");
        this._nextLink.className = "s-diag-view-nav-next";
        this._nextLink.setAttribute("data-s-picker", "next");
        this._nextLink.style.display = "none";
        this._nextLink.title = this.localize.diag_next_tooltip;
        this._nextLink.textContent = this.localize.diag_next;
        diagNavBar.appendChild(this._nextLink);
        
        // links bar (_core child)
        this._diagViewLinks = document.createElement("div");
        this._diagViewLinks.className = "s-diag-view-links-bar";
        this.$$diagViewLinks = $(this._core.appendChild(this._diagViewLinks));
        
        this._expandLink = document.createElement("a");
        this._expandLink.className = "s-diag-view-expander";
        this._expandLink.setAttribute("data-s-picker", "diag-expand");
        this._expandLink.style.display = "none";
        this._core.appendChild(this._expandLink);
        
        // diag opening tab (used when diagnoses panel is closed) ($$item child)
        this._diagOpener = document.createElement("a");
        this._diagOpener.className = "s-diag-view-opener";
        this._diagOpener.setAttribute("data-s-picker", "diag-opener");
        this._diagOpener.textContent = this.localize.diag_opener;
        this._diagOpener.style.display = "none";
        
        html = document.createElement("div");
        html.className = "s-diag-view-open";
        html.appendChild(this._diagOpener);
        this.$$item[0].appendChild(html);
        
        this._bindEvents(true);
        
        this.pageScroller = new AreaScroller(this.page.$$body);
        this.diagPanelScroller = new AreaScroller($(this._content));
    },
    _bindEvents: function(bind){
        var self = this;
        if (self.$$item) {
            if (bind) {
                self.$$item.delegate("a[data-s-picker]", "click", function(){
                    switch (this.getAttribute("data-s-picker")) {
                        case "close-msg":
                            self._onDiagnoseClose($(this));
                            break;
                        case "field-msg":
                            var field = document.controller.findItem($(this));
                            if (field) {
                                self._focusField(field);
                                self.onNavigationClick(0, field);
                            }
                            break;
                        case "diag-close":
                            self.$$item.hide();
                            self.cleanGlobalDiagnoses();
                            /*
                         if (self._core) {
                         self._core.style.display = "none";
                         if (self._diagOpener) {
                         document.site.setZIndex(self._diagOpener);
                         self._diagOpener.style.display = "inline-block";
                         }
                         self._fitPageToContainer();
                         }*/
                            break;
                        case "diag-opener":
                            self._onDiagOpenerClick();
                            break;
                        case "diag-expand":
                            self._onDiagExpand();
                            break;
                        case "expand":
                            $(this).parent().find("span").toggle();
                            self._fitPageToContainer();
                            break;
                        case "next":
                            self.onNavigationClick(this);
                            break;
                            
                    }
                    return false;
                });
            }
            else {
                self.$$item.undelegate();
            }
        }
    },
    _onDiagExpand: function(){
        // expand link text value for diagnose panel closed state
        var moreText = (this.total) - 1 == 0 ? this.localize.diag_expand : (this.total - 1) + this.localize.diag_expand;
        
        // getting all diagnoses in diagnose panel
        var diagMsg = this.$$body.find("div.s-diag-view-msg");
        // precondition 1 : is a diag focused ?
        var diagFocused;
        for (var ii = 0; ii < diagMsg.length; ii++) {
            if (diagMsg[ii].children[0].className.indexOf("s-diag-view-msg-focus") > -1) {
                diagFocused = ii;
            }
        }
        // precondition 2 : was expand link on diagnose panel opened/closed state ?
        // expand link previously on diagnose panel opened state
        if (this._expandLink.textContent.indexOf(this.localize.diag_collapse) > -1) {
        
            // precondition 1 true, showing previous diag focused
            if (diagFocused != undefined) {
                for (var ii = 0; ii < diagMsg.length; ii++) {
                    diagMsg[ii].style.display = "none";
                }
                diagMsg[diagFocused].style.display = "block";
            }
            // precondition 1 false, showing first diag 
            else {
                for (var ii = 0; ii < diagMsg.length; ii++) {
                    diagMsg[ii].style.display = ii == 0 ? "block" : "none";
                }
            }
            this._expandLink.textContent = moreText;
            
        }
        // expand link previously on diagnose panel closed state, show all diagnoses
        else {
            for (var ii = 0; ii < diagMsg.length; ii++) {
                diagMsg[ii].style.display = "block";
            }
            this._expandLink.textContent = this.localize.diag_collapse;
        }
        this._fitPageToContainer();
    },
    onNavigationClick: function(navLink, field){
        var index;
        if (this._fieldDiagnoses) {
            // getting index of previously focused diagnose if any
            for (var ii = 0; ii < this._fieldDiagnoses.length; ii++) {
                if (this._fieldDiagnoses[ii].label.className.indexOf("s-diag-view-msg-focus") > -1) {
                    index = ii;
                    break;
                }
            }
            // if navigation from direct field focus and not from "next" button
            // getting corresponding index to update focus in diagnose panel
            if (!navLink && field) {
                for (var ii = 0; ii < this._fieldDiagnoses.length; ii++) {
                    if (this._fieldDiagnoses[ii].field.id == field.id) {
                        index = ii;
                        break;
                    }
                }
            }
            else {
                // update index
                if (index != undefined) {
                
                    if (index == (this._fieldDiagnoses.length - 1)) {
                        index = 0;
                    }
                    else {
                        index++;
                    }
                }
                // set index value
                else {
                    index = 0;
                }
                
                // focus corresponding field
                this._focusField(this._fieldDiagnoses[index].field);
            }
            // focus corresponding diag
            for (var ii = 0; ii < this._fieldDiagnoses.length; ii++) {
                if (ii == index) {
                    document.site.toggleClass(this._fieldDiagnoses[index].label, "s-diag-view-msg-focus", true);
                }
                else {
                    document.site.toggleClass(this._fieldDiagnoses[ii].label, "s-diag-view-msg-focus");
                }
            }
            this.diagPanelScroller.scrollToTarget($(this._fieldDiagnoses[index].label).parent(), true);
            
            // if in collapse mode, show focused diag
            if (this._expandLink.textContent.indexOf(this.localize.diag_expand) > -1) {
                // getting all diagnoses in diagnose panel
                var diagMsg = [];
                diagMsg = this.$$body.find("div.s-diag-view-msg");
                for (var ii = 0; ii < diagMsg.length; ii++) {
                    diagMsg[ii].style.display = "none";
                }
                $(this._fieldDiagnoses[index].label).parent().show();
            }
            return false;
        }
        
    },
    _onDiagOpenerClick: function(){
        if (this._diagOpener && this._core) {
            this._core.style.display = "inline-block";
            this._diagOpener.style.display = "none";
            this._fitPageToContainer();
        }
        return false;
    },
    _onDiagnoseRemoval: function(){
        // update self.total, update expandLink text value, hide $$item if no diag left
        // getting all diagnoses in diagnose panel
        var diagMsg = this.$$body.find("div.s-diag-view-msg");
        // clean if last dialog removed
        if (diagMsg.length == 1) {
            this._emptyViewer();
        }
        else {
            this.total = diagMsg.length;
            // if diagnoses panel was collapsed, update expand link text or hide it
            if (this._expandLink.textContent.indexOf(this.localize.diag_expand) != -1) {
                diagMsg[0].style.display = "block";
                if (this.total == 1) {
                    this._expandLink.style.display = "none";
                }
                else {
                    this._expandLink.textContent = (this.total - 1) + this.localize.diag_expand;
                }
            }
            // if diagnoses panel was expanded, remove expand link if one diagnose left
            else {
                if (this.total == 1) {
                    this._expandLink.style.display = "none";
                }
            }
        }
        this._fitPageToContainer();
    },
    _onDiagnoseClose: function($$closeButton){
        // update self.total, update expandLink text value, hide $$item if no diag left
        var diagMsg = this.$$body.find("div.s-diag-view-msg");
        var isLastDialog = diagMsg.length == 1 ? true : false;
        $$closeButton.parent().remove();
        // clean if last dialog removed
        if (isLastDialog) {
            this._emptyViewer();
        }
        else {
            this.total--;
            // if diagnoses panel was collapsed, update expand link text or hide it
            if (this._expandLink.textContent.indexOf(this.localize.diag_expand) != -1) {
                diagMsg[1].style.display = "block";
                
                if (this.total == 1) {
                    this._expandLink.style.display = "none";
                }
                else {
                    this._expandLink.textContent = (this.total - 1) + this.localize.diag_expand;
                }
            }
            // if diagnoses panel was expanded, remove expand link if one diagnose left
            else {
                if (this.total == 1) {
                    this._expandLink.style.display = "none";
                }
            }
        }
        this._fitPageToContainer();
        return false;
    },
    _focusField: function(field){
        if (!(field.focus && field.focus())) {
            this.pageScroller.scrollToTarget(field.$$item, true);
        }
    },
    _updateFieldFocusHandling: function(field){
        // remove field from viewer._fieldDiagnoses array
        if (this._fieldDiagnoses) {
            var index;
            // getting index of field to remove
            for (var ii = 0; ii < this._fieldDiagnoses.length; ii++) {
                index = this._fieldDiagnoses[ii].field.id == field.id ? ii : index;
                if (index != undefined) {
                    break;
                }
            }
            // removing field from array
            if (index != undefined) {
                this._fieldDiagnoses.splice(index, 1);
            }
        }
        this.numFocusedField = 0;
    },
    cleanGlobalDiagnoses: function(){
        if (this.$$body) {
            var diagMsg = this.$$body.find("div.s-diag-view-msg");
            if (diagMsg.length > 0) {
                var globalDiag = [];
                for (var ii = 0; ii < diagMsg.length; ii++) {
                    if ($(diagMsg[ii]).find("a.s-diag-view-msg-link").length < 1) {
                        globalDiag.push(diagMsg[ii]);
                    }
                }
                for (var ii = 0; ii < globalDiag.length; ii++) {
                    globalDiag[ii].parentNode.removeChild(globalDiag[ii]);
                }
            }
        }
    },
    drawLinks: function(slot, message, page){
        page = page || this.page;
        if (!this._menus) {
            this._menus = page.loadNewItem(slot, {
                $category: "menus",
                $isOwner: true,
                $skin: "s-diag-view-links"
            });
            this._menus.isDiagnoseMenus = true;
        }
        this._menus.ensurePrivateMenus(message);
    },
    emptyDiagnoseByFieldId: function(fieldId){
        // remove diagnoses
        this.$$body.find("a[data-s-menu=" + fieldId + "]").parent().parent().remove();
        // update diagnoses panel ui accordingly
        this._onDiagnoseRemoval();
    },
    showDiagnoses: function(message, page, options){
        var self = this;
        // clean if no message
        if (!message || (!message.field && !message.$diagnoses)) {
            self._emptyViewer();
            if (options) {
                // for further usage ?
            }
        }
        else {
            // initialize
            if (!self._core) {
                self._initialize();
            }
            if (self.onSuccess) {
                self._cleanSuccessStyle();
            }
            if (options) {
                // for further usage ?
            }
            // append message links
            if (message.$links || message.$actions) {
                // links are most of the times supposed to be sent when there is success after validation
                // flaging success mode, cleaning body 
                if (message.$diagnoses.length == 1 && message.$diagnoses[0].severity == "success") {
                    self.onSuccess = true;
                    self._emptyViewer();
                }
                self.drawLinks(self._diagViewLinks, message, page);
            }
            
            // array used for field focus purpose
            self._fieldDiagnoses = self._fieldDiagnoses || [];
            
            // draw diagnoses
            if (message.$diagnoses && message.$diagnoses.length > 0) {
                if (message.field) {
                    self._cleanDiagnoses(message.field);
                    message.field.ensureDiagnoseSlot();
                }
                var group;
                for (var ii = 0, jj = message.$diagnoses.length; ii < jj; ii++) {
                    var $diagnose = message.$diagnoses[ii];
                    var severity = $diagnose.severity ? $diagnose.severity.toLowerCase() : "";
                    group = self._groupsMap[severity] || self._groupsMap["error"];
                    if (message.field) {
                        message.field.drawDiagnose($diagnose, group);
                    }
                    else {
                        group.drawDiagnose($diagnose);
                    }
                }
                if (message.field) {
                    message.field.domDiagnose.style.visibility = "";
                    if (group) {
                        message.field.toggleDiagnose("s-" + group.severity, true);
                    }
                }
            }
            else {
                self._cleanDiagnoses(message.field);
            }
            // show/refresh diagnoses panel
            if (!options || !options.noViewer) {
                self._refreshViewer(options, page);
                if (message.autoHide) {
                    setTimeout(function(){
                        self._emptyViewer();
                    }, message.autoHide.autoHideTimeOut || 4000);
                }
            }
        }
    },
    _cleanDiagnoses: function(field){
        this._emptyGroups(field);
        if (field && field.domDiagnose) {
            field.emptyDiagnoseSlot();
            this._updateFieldFocusHandling(field);
        }
    },
    dispose: function(){
        if (this._groups) {
            for (var ii = 0, jj = this._groups.length; ii < jj; ii++) {
                var group = this._groups[ii];
                delete this._groupsMap[group.severity];
                group.dispose();
            }
        }
        this._bindEvents(false);
        if (this.pageScroller) {
            this.pageScroller.dispose();
        }
        if (this.diagPanelScroller) {
            this.diagPanelScroller.dispose();
        }
        this.pageScroller = this.diagPanelScroller = null;
        this._groups = this._groupsMap = null;
        this.page = this.total = this.onSuccess = null;
    }
});
