	close: "ESC",
	escape: 'ESC',
	contextMenu: "ESC M, ESC F4",
	searchBox: "ESC F7, ESC S",
	tunnel: "ESC F9",
	openDatePick: "ALT DOWN",
	openTunnel: "ESC F9",
	openLookup: "ESC L, ESC F12",
