function EmailField() {}

exports.EmailField = helpers.defineClass(EmailField, Field, {
	initialize: function() {
		this.$isDetailLinkDisabled = true;
	},
	validateType: function(errors, value) {
		if (value) {
			var atpos = value.indexOf("@");
			var dotpos = value.lastIndexOf(".");
			if (atpos < 1 || dotpos < atpos + 2 || dotpos + 2 >= value.length) {
				errors.push(this.localize.fieldEmailInvalid);
			}
		}
	},
	createHtmlInput: function() {
		var input = document.createElement("input");
		if (!this.$item.$isFilterMode) {
			input.setAttribute("type", "email");
		}
		document.site.setSpecificAttributes(input);
		return input;
	},
	setDataValue: function(value) {
		this.currentValue = value;
		if (this.$isEditMode) {
			this.setInputValue(this.currentValue || "");
		} else {
			document.site.dom.empty(this.fieldValue);
			this.fieldHelper.setValueLength(this, (value = value || "").length);
			var link = document.createElement("a");
			link.className = "s-email-link";
			link.setAttribute("rel", "nofollow");
			link.setAttribute("href", "mailto:" + value);
			link.textContent = value;
			this.fieldValue.appendChild(link);
			this.showContextMenus();
		}
	}
});