"use strict";
var helpers = require('syracuse-core/lib/helpers');
var RecordArticle = require("syracuse-ui/lib/field/array/recordArticle").RecordArticle;

function GridRecord() {}

exports.GridRecord = helpers.defineClass(GridRecord, RecordArticle, {
	applyChange: function(newData) {
		RecordArticle.prototype.applyChange.call(this, newData);
		this.list.cardDecorator.onRecordApplyChange(this);
	},
	setMenusBoxCol: function($quickAction, add, width) {
		var col = this.list.cols.$menusBox;
		if (add) {
			if (!col.$quickActions[$quickAction]) {
				col.$quickActions[$quickAction] = true;
				col.widthValue = (col.width += width);
				col.groupHead.style.width = col.groupBody.style.width = col.widthValue + "px";
			}
		} else {
			if (!$quickAction) {
				col.$quickActions = {};
				col.widthValue = col.width = 0;
				col.groupHead.style.width = col.groupBody.style.width = col.widthValue + "px";
			} else {
				if (col.$quickActions[$quickAction]) {
					delete col.$quickActions[$quickAction];
					col.widthValue = (col.width -= width);
					col.groupHead.style.width = col.groupBody.style.width = col.widthValue + "px";
				}
			}
		}
	},
	showUnbindFieldDiagnose: function($bind, $diagnoses) {
		if (this.list.cardDecorator.showUnbindFieldDiagnose(this, $bind, $diagnoses)) {
			RecordArticle.prototype.showUnbindFieldDiagnose.call(this, $bind, $diagnoses);
		}
	},
	disposeCard: function() {
		this.layoutSlot = this.layoutContent = this.domItem = this.body = this.$$body = this.rowCardRow = this.rowCardCell = this.rowCardOpener = this.cardItem = null;
	},
	dispose: function() {
		this.disposeCard();
		this.treeNode = this.dataRow = this.domItem = this.popupCardItem = null;
		this._selectorCheck = this.titleSlot = null;
		RecordArticle.prototype.dispose.call(this);
	},
	reorderItem: function(targetRecord, isAfter) {
		if (targetRecord) {
			targetRecord.dataRow.parentNode.insertBefore(this.dataRow, isAfter ? targetRecord.dataRow.nextSibling : targetRecord.dataRow);
		} else {
			if (isAfter) {
				this.list.body.appendChild(this.dataRow);
			} else {
				this.list.body.insertBefore(this.dataRow, this.list.body.firstChild);
			}
		}
		if (this.rowCardRow) {
			this.dataRow.parentNode.insertBefore(this.rowCardRow, this.dataRow.nextSibling);
		}
	},
	onFieldDiagnoseChange: function(add) {
		this.list.onFieldDiagnoseChange(add);
	},
	onChildFieldShowDiagnoses: function(field, $diagnoses) {
		this.list.cardDecorator.onChildFieldShowDiagnoses(this, field, $diagnoses);
	},
	remove: function() {
		if (this.$$item) {
			this.$$item.remove();
		}
		if (this.rowCardRow) {
			document.site.removeDomChild(this.rowCardRow);
		}
	},
	applyReorderCapability: function(reorder) {
		this.hasReorderCapability = reorder;
		if (reorder) {
			this.reorderPicker = this.rowIndexPicker;
			this.reorderPicker.syraReorderRecordId = this.list.id;
			document.site.toggleClass(this.rowIndexPicker, "s-grid-record-reorder", true);
			document.site.ddManager.setDragSpot(this.rowIndexPicker, true);
		} else {
			if (this.reorderPicker) {
				delete this.reorderPicker.syraReorderRecordId;
				delete this.reorderPicker;
				document.site.toggleClass(this.rowIndexPicker, "s-grid-record-reorder", false);
				document.site.ddManager.setDragSpot(this.rowIndexPicker, false);
			}
		}
	},
	appendRowIndexCell: function(slot) {
		this.rowIndexPicker = document.createElement("td");
		this.rowIndexPicker.className = this.list.gridCss.rowIndex;
		if (this.list.$item.$selectByRowIndex) {
			this.rowIndexPicker.className += " s-list-row-index-selector";
			this.rowIndexPicker.setAttribute("data-s-picker", "list-selector");
		}
		if (this.list.$capability && this.list.$capability.reorder) {
			this.applyReorderCapability(true);
		}
		slot.appendChild(this.rowIndexPicker);
		this.renderRowIndex();
		if (this.list.treeDecorator) {
			// remove border between line 
			this.rowIndexPicker.className += " s-list-no-border";
		}
		return this.rowIndexPicker;
	},
	appendRecordSelector: function(slot) {
		var td = document.createElement("td");
		td.className = this.list.gridCss.cell;
		this._selectorCheck = this.list.selector.appendToRecord(slot.appendChild(td));
		return td;
	},
	onSelectMouseEvent: function(event) {
		document.site.toggleClass(this.dataRow, "s-record-over", event.type == "mouseenter");
	},
	renderAltStyle: function() {
		if (this.list.$item.$alternateStyle) {
			document.site.toggleClass(this.dataRow, "s-list-row-alt", this.$recordIndex % 2);
		}
	},
	drawBox: function() {
		this.$isVerticalDirection = true;
		this.dataRow = document.createElement("tr");
		this.dataRow.setAttribute("data-s-record", this.$uuid);
		if (!this.list.treeDecorator) {
			if (this.isInsert) {
				if (this.list.records[this.$recordIndex]) {
					this.$$item = $(this.dataRow);
					this.$$item.insertBefore(this.list.records[this.$recordIndex].$$item);
				} else {
					//hack temp pour teddy                    
					this.list.body.appendChild(this.dataRow);
				}
				delete this.isInsert;
			} else {
				this.list.body.appendChild(this.dataRow);
			}
		}
		this.$$item = $(this.dataRow);
		this.dataRow.className = this.list.$skin + "-row";
		if (this.list.treeDecorator) {
			this.list.treeDecorator.appendCellNode(this, this.dataRow);
		} else {
			this.appendRowIndexCell(this.dataRow);
		}
		if (this.list.cardDecorator.isRowCardMode) {
			this.dataRow.appendChild(this.list.cardDecorator.appendRowCardOpener(this));
		}
		if (this.list.selector.useColumnSelector) {
			this.appendRecordSelector(this.dataRow);
		}
		if (!this.list.$item.$isMenuRecordHidden) {
			this.menusSlot = document.createElement("td");
			this.menusSlot.className = this.list.gridCss.cell + " " + this.list.$skin + "-actions-cell";
			this.dataRow.appendChild(this.menusSlot);
			if (this.list.treeDecorator) {
				this.menusSlot.className += " s-list-tree-cell";
			} else {
				this.menusSlot.className += " s-record-actions";
			}
			this.list.applyRecordCapabilities(this);
		}
		if (this.list.selector.isRowMode) {
			this.dataRow.setAttribute("data-s-picker", "list-selector");
			this.dataRow.className += " s-list-selector-row";
			this.dataRow.setAttribute("data-s-inout", this.id);
		}
		var $items = this.list.$item.$layout.$items;
		var cssCell = this.list.gridCss.cell;
		if (this.list.treeDecorator) {
			cssCell += " s-list-no-border";
		}
		for (var ii = 0, jj = $items.length; ii < jj; ii++) {
			var $item = $items[ii];
			var $field = this.list.$fields[$item.$bind];
			if ($field && !$field.$isExcluded) {
				var td = document.createElement("td");
				td.className = cssCell;
				var col = this.list.cols[$item.$bind];
				if (col.$isHidden) {
					td.style.display = "none";
				}
				this.dataRow.appendChild(td);
				if (col.$isFixed) {
					td.className += " s-grid-col-fixed";
					if (col == this.list.lastColFixed) {
						td.className += " s-grid-col-fixed-last";
					}
				}
				this.page.loadNewItem(this.dataRow.appendChild(td), {
					$bind: $item.$bind,
					$isCellChild: true,
					$inplace: true
				}, this);

			}
		}
		if (this.list.popupCardBuilder) {
			this.list.popupCardBuilder.togglePopupCardMenu(this, true);
		}
	},
	onMenuClick: function(menuItem) {
		var self = this;
		var $newIndex, $startIndex;
		switch (menuItem.$bind) {
			case "$first":
				$startIndex = 1;
				$newIndex = 0;
				break;
			case "$previous":
				$newIndex = (self.list.pagging.$startIndex - 1) + (self.$recordIndex - 1);
				break;
			case "$next":
				$newIndex = (self.list.pagging.$startIndex - 1) + (self.$recordIndex + 1);
				break;
			case "$last":
				$newIndex = this.list.dataset.length - 1;
				break;
			case "$recordCard":
				this.list.popupCardBuilder.togglePopup(this);
				return false;
			default:
				return RecordArticle.prototype.onMenuClick.call(this, menuItem);
		}
		$startIndex = (Math.floor($newIndex / self.list.pagging.getItemsPerPage()) * self.list.pagging.getItemsPerPage()) + 1;
		setTimeout(function() {
			var list = self.list;
			if ($startIndex != list.pagging.$startIndex) {
				var options = list.clientFetchOptions || {};
				(options.params = options.params || {}).startIndex = $startIndex;
				list.fetchClientRecordSet(options);
			}
			list.selector.select(list.clientDataset[$newIndex].$uuid, true);
		}, 10);
		return false;
	},
	toggleFocus: function(focused) {
		this.isFocused = focused;
		document.site.toggleClass(this.dataRow, "s-record-focused", focused);
	},
	highlightSelection: function(selected) {
		document.site.toggleClass(this.dataRow, "s-list-record-selected", selected);
		var record = this.list.getFocusedRecord(false);
		if (record) {
			record.toggleFocus(false);
		}
		this.list.cardDecorator.onSelectRecord(this, selected);

		if (this._selectorCheck) {
			this._selectorCheck.checked = selected;
		}
		if (selected) {
			if (!this.list.selector.isMulti) {
				this.list.scroller.scrollToItem(this.dataRow);
			}
		}
	},
	applyShortCuts: function(shortcuts, event, focusField) {
		if (shortcuts["delete"] || shortcuts.insert) {
			var bind = shortcuts["delete"] ? "$delete" : "$create";
			if (this.menuItems && this.menuItems[bind] && this.menuItems[bind][0]) {
				this.menuItems[bind][0].click(event);
				return true;
			}
		}
		return false;
	},
	getNextPreviousField: function(next, $bind) {
		var found;
		for (var ii = 0, jj = this.list.orderCols.length; ii < jj; ii++) {
			if (!this.list.orderCols[ii].$isHidden && this.list.orderCols[ii].$bind) {
				if (next) {
					if (this.list.orderCols[ii].$bind == $bind) {
						found = $bind;
					} else {
						if (found) {
							found = this.list.orderCols[ii].$bind;
							break;
						}
					}
				} else {
					if (this.list.orderCols[ii].$bind == $bind) {
						found = found || $bind;
						break;
					} else {
						found = this.list.orderCols[ii].$bind;
					}
				}
			}
		}
		return found;
	}
});