"use strict";
var helpers = require('syracuse-core/lib/helpers');

function _getTitleChoice(datasetValue, colProperties) {
	var result = "";
	if (colProperties.$enum) {
		for (var ii = 0; ii < colProperties.$enum.length; ii++) {
			var $item = colProperties.$enum[ii];
			if ($item.$value == datasetValue) {
				result = $item.$title;
				break;
			}
		}
	}
	return result;
}

function _compare(value, datasetValue, colProperties, incrementalSearch) {
	switch (colProperties.$type) {
		case "application/x-date":
		case "application/x-time":
		case "application/x-datetime":
		case "application/x-integer":
		case "application/x-decimal":
		case "application/x-real":
		case "application/x-quantity":
			if (!datasetValue.toLowerCase) {
				datasetValue = new String(datasetValue);
			}
		case "application/x-string":
			if (incrementalSearch) {
				return (datasetValue.toLowerCase().indexOf(value.toLowerCase()) == 0);
			}
			return (datasetValue.toLowerCase().indexOf(value.toLowerCase()) != -1);
		case "application/x-choice":
			if (incrementalSearch) {
				return (_getTitleChoice(datasetValue, colProperties).toLowerCase().indexOf(value.toLowerCase()) == 0);
			}
			return (_getTitleChoice(datasetValue, colProperties).toLowerCase().indexOf(value.toLowerCase()) != -1);
		case "application/x-icon":
			return false;
		case "application/x-boolean":
			return false;
	}
	return false;
}

function _startIndexes(searcher, next) {
	var startIndex = {
		row: 0,
		col: 0
	};
	var changeLine = false;
	if (searcher.start && searcher.start.$bind) {
		for (var ii = 0; ii < searcher.orderCols.length; ii++) {
			if (searcher.orderCols[ii].$bind == searcher.start.$bind) {
				if (next) {
					startIndex.col = (ii < searcher.orderCols.length - 1) ? (ii + 1) : 0;
					changeLine = !startIndex.col;
				} else {
					startIndex.col = (ii == 0) ? (searcher.orderCols.length - 1) : ii - 1;
					changeLine = !(startIndex.col == ii - 1);
				}
				searcher.start.$bind = searcher.orderCols[startIndex.col].$bind;
				break;
			}
		}
	}
	if (searcher.start && searcher.start.$uuid != undefined) {
		var list = searcher.list;
		for (var ii = 0; ii < list.clientDataset.length; ii++) {
			if (list.clientDataset[ii].$uuid == searcher.start.$uuid) {
				if (changeLine && next) {
					startIndex.row = (ii < list.clientDataset.length - 1 ? ii + 1 : ii);
				} else
				if (changeLine && !next) {
					startIndex.row = (ii > 0 ? ii - 1 : ii);
				} else {
					startIndex.row = ii;
				}
				searcher.start.$uuid = list.clientDataset[startIndex.row].$uuid;
				break;
			}
		}
	}
	return startIndex;
}


function _run(searcher, value, next, notFound, incrementalSearch) {
	var startIndexes = _startIndexes(searcher, next);
	var toRow = (next ? searcher.list.clientDataset.length : -1);
	var toCol = (next ? searcher.orderCols.length : -1);
	var found, dataset;
	for (var ii = startIndexes.row;
		(next && ii < toRow) || (!next && ii > toRow);
		(next ? ii++ : ii--)) {
		for (var jj = startIndexes.col;
			(next && jj < toCol) || (!next && jj > toCol);
			(next ? jj++ : jj--)) {
			dataset = searcher.list.treeDecorator ? searcher.list.records[ii].dataset : searcher.list.clientDataset[ii];
			if (dataset[searcher.orderCols[jj].$bind] && _compare(value, dataset[searcher.orderCols[jj].$bind], searcher.orderCols[jj], incrementalSearch)) {
				searcher.setStart(dataset.$uuid, searcher.orderCols[jj].$bind, dataset.$serverIndex);
				return searcher.start;
			}
		}
		startIndexes.col = next ? 0 : searcher.orderCols.length - 1;
		toCol = next ? searcher.orderCols.length : -1;
	}
	if (!notFound) {
		if (searcher.start && searcher.start.$uuid) {
			searcher.start = {};
		}
		return _run(searcher, value, next, true, incrementalSearch);
	}
	searcher.start = {};
	return null;
}

function Searcher() {}

exports.Searcher = helpers.defineClass(Searcher, null, {
	unload: function() {
		if (this._itemSearchField) {
			this.list.removeItem(this._itemSearchField);
			delete this._itemSearchField;
		}

		syra_site.dom.removeChild(this.domItem);
		this.domItem = this._itemSearchField = this._fieldSlot = null;
	},
	load: function(list) {
		this.list = list;
		this.start = {};
		this.list.onSearchClick = function(event, btn) {
			this.searcher && this.searcher.onClick(this, btn.syraValue, event);
		};

		this.domItem = document.createElement("div");
		this.domItem.style.display = "none";
		this.domItem.className = "s-list-search";
		this.list.topbar.appendChild(this.domItem);
		if (syra_local.flSearch) {
			var label = document.createElement("label");
			label.className = "s-list-search-label";
			this.domItem.appendChild(label).textContent = syra_local.flSearch + ":";
		}

		var btn = syra_menus.addIconButton("", "s-list-search-picker", "onSearchClick", null, "search");
		btn.syraValue = "$searchCollapse";
		this.domItem.appendChild(btn);

		this._fieldSlot = document.createElement("div");
		this._fieldSlot.className = "s-list-search-value";
		this._fieldSlot.style.display = "none";

		this._itemSearchField = this.list.page.loadNewItem(this.domItem.appendChild(this._fieldSlot), {
			$bind: "$search",
			$css: "s-list-search-field",
			$category: "field",
			$isEditMode: true,
			$inplace: true,
			$field: {
				$type: "application/x-string",
			}
		}, this.list);

		var btn = syra_menus.addIconButton("Next", "s-list-search-goto", "onSearchClick", null, "expand_row");
		btn.syraValue = "$searchNext";
		this._itemSearchField._dataValue.appendChild(btn);
		btn = syra_menus.addIconButton("Previous", "s-list-search-goto", "onSearchClick", null, "collapse_row");
		btn.syraValue = "$searchPrevious";
		this._itemSearchField._dataValue.appendChild(btn);
		this.list.topbar.appendChild(this.domItem);
		if (this.list.$item.$graphPosition == "front") {
			this.toggle(false);
		}
	},
	toggle: function(show) {
		syra_site.dom.display(this.domItem, show !== false);
	},
	onClick: function($bind, event) {
		switch ($bind) {
			case "$searchCollapse":
				if (this._fieldSlot) {
					if (this._fieldSlot.style.display == "none") {
						this._fieldSlot.style.display = "";
						this._itemSearchField.focus();
					} else {
						this._fieldSlot.style.display = "none";
					}
				}
				break;
			case "$searchNext":
			case "$searchPrevious":
				var found, value = this._itemSearchField.getInputValue();
				if (value === "") {
					return false;
				}
				this._ensureOrderCols();
				if (found = _run(this, value, ($bind == "$searchNext"))) {
					this.list.page.externalAdapter.onSearchClickEvent({
						field: this.list,
						found: found,
						event: event,
						doEvent: function() {
							this.list.setFocus(true, found.$bind, found.$serverIndex);
						}
					});
				}
				break;
		}
	},
	_ensureOrderCols: function() {
		var colProperties = {};
		this.orderCols = [];
		var orderCols = this.list.builder.allColumns;
		for (var ii = 0; ii < orderCols.length; ii++) {
			var col = orderCols[ii];
			if (col.key == "$treeview") {
				col = {
					$bind: this.list.$prototype.$treeview.$bindings.$description
				};
			}
			if (!col.$isHidden && col.$bind) {
				colProperties = {};
				colProperties.$bind = col.$bind;
				colProperties.$type = this.list.$fields[col.$bind].$type;
				if (this.list.$fields[col.$bind].$value && this.list.$fields[col.$bind].$value.$enum) {
					colProperties.$enum = this.list.$fields[col.$bind].$value.$enum;
				}
				this.orderCols.push(colProperties);
			}
		}
	},
	incrementalSearch: function(event, incrementalSearchValue) {
		if (incrementalSearchValue === "") {
			return {};
		}
		this._ensureOrderCols();
		return _run(this, incrementalSearchValue, true, null, true);
	},
	setStartstart: function($uuid, $bind, $serverIndex) {
		this.start = {
			$uuid: $uuid,
			$bind: $bind,
			$serverIndex: $serverIndex
		};
	}
});