"use strict";
var helpers = require('syracuse-core/lib/helpers');
var Field = require('syracuse-ui/lib/field/field').Field;
var FilterBlock = require("./filterBlock").FilterBlock;
var fieldHelper = require('syracuse-ui/lib/field/helpers/fieldHelper');

function FilterWidget() {}


exports.FilterWidget = helpers.defineClass(FilterWidget, Field, {
	loadBox: function() {
		syra_menus.initialize(this);
		this.ensureEditMode();

		this.$skinField = this.$skin = this.$item.$skin || (this.$item.$inplace ? "s-inplace" : "s-field");
		this.domItem = this.layoutSlot;
		if (!this.$item.$inplace) {
			this.domItem = this.layoutSlot.appendChild(document.createElement("div"));
		}

		// Align label at top if edit mode
		if (this.$isEditMode) {
			this.$skinField = this.$skin = this.$item.$skin || (this.$item.$inplace ? "s-inplace" : "s-field-filter");
			this.$skinInput = this.$skin + "-input";
			if (this.$item.$isTopLabelAlignment == null) {
				this.$item.$isTopLabelAlignment = true;
			}
		}

		this._initCss();

		this.domItem.className += " " + this._$cssField;

		this.appendTitle();
		this._createValueContainer();
		this.isField = true;
		this.setFieldId(this.domItem);
		this.domItem.syrainout = this.domItem.syraItem = this.id;
		this.domItem.syra_field_bind = this.$item.$bind || "";

		this.setTitle(this.$item.$title || this.$field.$title);
		this.articleParent.bind(this, this.$item.$bind);
	},
	applyMetaData: function(metaData, options) {
		var self = this;
		Field.prototype.applyMetaData.call(self, metaData, options);
		if (metaData) {
			if (metaData.$isReadOnly !== undefined) {
				self.$isReadOnly = metaData.$isReadOnly;
			}
			if (metaData.$isEditMode !== undefined) {
				self.$isEditMode = metaData.$isEditMode;
			}
			if (metaData.$isDisabled !== undefined) {
				self.$isDisabled = metaData.$isDisabled;
			}

			if (metaData.$item) {
				self._applyItemMetaData(metaData, options);
			}
			if (metaData.$item || metaData.$isEditMode || metaData.$isDisabled || metaData.$isReadOnly) {
				self._applyStateMetaData(metaData, options);
			}
			//
			if (self.$isEditMode) {
				var p_url = syra_site.expressionMaker.parse(self.articleParent, (metaData.$links && metaData.$links.$prototype && metaData.$links.$prototype.$url) || (self.$field.$links && self.$field.$links.$prototype && self.$field.$links.$prototype.$url));
				if ((!self._prototypeUrl && p_url) || (self._prototypeUrl && (self._prototypeUrl != p_url))) {
					self._prototypeUrl = p_url;
					syra_controller.sendRequest(self.articleParent, {
						$location: {
							$url: p_url
						}
					}, function(data, response, requestUrl) {
						var proto = {
							$item: data
						};
						var dps = ((data.$properties && data.$properties.$resources && data.$properties.$resources.$item) || data).$properties;
						var pp = proto.$item.$properties = {};
						Object.keys(dps || {}).forEach(function(p) {
							var dp = dps[p];
							if ((dp.$capabilities || "").indexOf("filter") >= 0) {
								pp[p] = dp;
							}
						});
						self._applyItemMetaData(proto, options);
						// apply state after filterBlock creation
						self._applyStateMetaData(metaData, options);
					});
				}
			}
		}
	},
	render: function() {

	},
	setDataValue: function(value) {
		this.sdataWhere = value;
		if (this.fieldValue) {
			this.fieldValue.textContent = value;
		}
		if (this.filterBlock) {
			this.filterBlock.setSDataWhere(value);
		}
		fieldHelper.setValueLength(this, value);
	},
	/* called by filterBlock on filter modification */
	setSDataWhere: function(sdataWhere) {
		// normalize sdataWhere
		sdataWhere = sdataWhere.replace(/\"/g, "'");
		if (this.$isEditMode) {
			if (this.fieldValue) {
				syra_site.dom.empty(this.fieldValue);
				this.fieldValue.textContent = sdataWhere;
			}
			if (sdataWhere !== undefined && (this.sdataWhere !== sdataWhere)) {
				this.notifyFieldChange(sdataWhere);
			}
			this.sdataWhere = sdataWhere;
		}
	},
	_initCss: function() {
		this._$cssField = (this.$item.$css) ? (this.$item.$css + " " + this.$skinField) : this.$skinField;
	},
	_createValueContainer: function() {
		this._dataValue = document.createElement("div");
		this._dataValue.className = this._$cssField + "-value";
		this.domItem.appendChild(this._dataValue);
		this.fieldValue = this._dataValue;
		// in table
		if (this.$item.$inplace) {
			return;
		}
		this._filterBlockContainer = document.createElement("div");
		this._filterBlockContainer.className = this._$cssField + "-fields";
		this.domItem.appendChild(this._filterBlockContainer);
	},
	_applyItemMetaData: function(metaData, options) {
		var $item = metaData && metaData.$item;
		if (!(this.$item.$inplace) && this.$isEditMode) {
			this.filterBlock = new FilterBlock();
			this.filterBlock.buildFilterBlock(this, this._filterBlockContainer, $item);
			this.filterBlock.setSDataWhere(this.sdataWhere);
		}
	},
	_applyStateMetaData: function(metaData, options) {
		if (this.filterBlock && metaData) {
			metaData = {};
			metaData.$isReadOnly = this.$isReadOnly;
			metaData.$isEditMode = this.$isEditMode;
			metaData.$isDisabled = this.$isDisabled;
			this.filterBlock.applyStateMetaData(metaData, options);
		}
	},
	dispose: function() {
		this.layoutParent = this.authoringNode = this.boxParent = this.articleParent = null;
		this.page = this.$field = this.$item = this.boxParent = null;
		this.layoutSlot = this.domItem = this._dataValue = null;
		this._core = this.input = this.currentValue = null;
		this.filterBlock && this.filterBlock.dispose();
	}
});