"use strict";
var helpers = require('syracuse-core/lib/helpers');
var Field = require('syracuse-ui/lib/field/field').Field;

function LinkField(){
}

exports.LinkField = helpers.defineClass(LinkField, Field, {
    setTitle: function($title){
        if (!this.domTitle) return;
        this.domTitle.className = "s-vignette-widget-menus-link-label";
        this.$titleValue = $title;
        delete this.titleText;
        var a = document.createElement("a");
        a.textContent = this.getTitle();
        a.setAttribute("href", "/syracuse-main/html/main.html?url=" + encodeURIComponent(this.$field.$url));
        this.domTitle.appendChild(a);
    },
});
