"use strict";
var helpers = require('syracuse-core/lib/helpers');
var Field = require('syracuse-ui/lib/field/field').Field;
var syraUtil = require('syracuse-ui/lib/fusion/core/client/sap/util').Syra;


function ProgressField(){
}
exports.ProgressField = helpers.defineClassEx(ProgressField, Field, {

    createHtmlInput_natif: function(){
      	var progress = document.createElement("progress");
        progress.setAttribute("value", "0");
        progress.setAttribute("max", "100");
        return progress     
    },
    createHtmlInput: function(){      
       	var progress = document.createElement("div");
        progress.className = "s-file-progress";
        this.progressValue = document.createElement("div");
        this.progressValue.className = "s-field-progress-value";
        progress.appendChild(this.progressValue);
        return progress
   
    },
    setDataValue : function(value, $bind){
    	 if(this.progressValue){
    	 	this.progressValue.style.width = ((this.$$input.width() * value)/100) + "px";
    	 }
    }
});
