"use strict";
var helpers = require('syracuse-core/lib/helpers');
var Field = require('syracuse-ui/lib/field/field').Field;
var renderer = require('syracuse-ui/lib/field/schedule/jqueryGantt');

var forEachKey = helpers.object.forEachKey;
var objectCopy = helpers.object.copy;
var objectClone = helpers.object.clone;

function Gantt() {}

exports.Gantt = helpers.defineClass(Gantt, Field, {
	setDataValue: function(value) {
		this.currentValue = value;
		renderer && renderer.setDataValue.call(this, value);
	},
	render: function() {
		var id = "a" + helpers.uuid.generate();
		this.$$dataValue.attr("id", id);
		renderer && renderer.render.call(this);
	}
});