exports.render = function() {
	var id = this.$$dataValue.attr("id");
	this.$$widgetBox = $('<div style="height: 400px; border: 1px solid #aaa">').attr('id', "gtt_" + id).addClass('gtt-container').appendTo(this.$$dataValue);
};

function _toSource(value) {
	return {
		name: "Example",
		desc: "Lorem ipsum dolor sit amet.",
		values: value.$events && value.$events.map(function(e) {
			return {
				to: "/Date(" + (e.start && (e.start.date || e.start.datetime)) + ")/",
				from: "/Date(" + (e.end && (e.end.date || e.end.datetime)) + ")/",
				desc: e.description,
				label: e.summary,
				customClass: "ganttRed",
				dataObj: {}
			}
		})
	};
};