var syraUtil = require('syracuse-ui/lib/fusion/core/client/sap/util').Syra;
var util = require('syracuse-ui/lib/fusion/tools/util');

var _evtProcFormula = {};
exports.FormulaEventHandling = _evtProcFormula;

/* "Formula wizard dialogs" events handling class */
_evtProcFormula.sap = function() {
	this.eventListeners = function() {
		return [
			["wdgt.link.click", onClick, true, true]
		];
	};

	function onClick(event) {
		var processed = undefined;
		alert("toto");
		// return false to let convergence main controller call is own event handling instance
		// return undefined to completly igone this event (it would be process by syracuse framework)
		// return true to intercept this event and register sap action (as usual)
		return false;
	}
};