var sapUtil = require('syracuse-ui/lib/fusion/core/client/sap/util').Fusion;
var syraUtil = require('syracuse-ui/lib/fusion/core/client/sap/util').Syra;
var util = require('syracuse-ui/lib/fusion/tools/util');
var requestActions = require('../sap/srvactions');
var keys = require('syracuse-ui/lib/fusion/tools/constant').keybordKey;
var svcFmt = require('syracuse-ui/lib/fusion/core/tools/format').svcFormatter;

var _evtProcWindow = {};
exports.WindowEventHandling = _evtProcWindow;

/* "Server page input" event handling class for "Window" */
_evtProcWindow.sap = function(){
    this.eventListeners = function(){
        return [
            ["wdgt.win.keydown", onKeyDown, true, true],
            ["wdgt.win.dblclick", onDblClick, true, true],
            ["wdgt.win.click", onClick, true, true],
            ["wdgt.win.close", onClose, true, true],
            ["wdgt.field.keydown", onFieldKeyDown, true, true],
            ["wdgt.field.click", onFieldClick, true, true],
            ["wdgt.field.picker", onFieldClickPicker, true, true],
            ["wdgt.field.change", onFieldChange, true, true],
            ["wdgt.field.calendarclosed", onDatePickerClose, true, true]
       ];
    };
      
    function onKeyDown(event){
        var processed = undefined, $$field = $(event.data.nativeEvt.target), $$cont, $itemMeta;
        var oldValue, fieldCxt, currValue, fieldInfo;
        if (event.target) {
            switch (event.data.nativeEvt.keyCode) {
                case keys["VK_TAB"]:
                case keys["VK_ESCAPE"]:
                    if (!($$cont = $$field.closest("." + syraUtil.pageSectionK.bar)) || $$cont.length == 0 || event.data.nativeEvt.target.tagName.toLowerCase() != "input") {
                        // TODO : le if ci-dessus permet de gérer les input dans liste gauche... mais pas bon pour autant... à reprendre!
                        if(event.data.nativeEvt.keyCode == keys["VK_TAB"]) {
                            event.result = {"$act": requestActions.getSrvAction(event.data.nativeEvt.shiftKey ? "SRV_CHAMP_PRECEDENT" : "SRV_CHAMP_SUIVANT")};
                        }
                        if(!event.noControl && event.data.nativeEvt.shiftKey || event.data.nativeEvt.keyCode == keys["VK_ESCAPE"]) {
                            // Restore original data sent by server if value does nor respect control/format pattern (that is what action "ESC" & "SRV_CHAMP_PRECEDENT" mean)
                            if ((fieldInfo = _getFieldInfo(event.target)) && (fieldCxt = this.sapController.getCurrInst())) {
                                currValue = fieldInfo.winModel.getValue(fieldInfo.fieldIst.xid, fieldInfo.fieldIst.nl);
                                if (currValue.v && fieldCxt.fmt && sapUtil.needToControlField(fieldInfo.type)) {
                                    /* Warning : code below may seem strange, but initialy, we adopted the same behaviour for "ESC" and "SHIFT_ESCAPE", i.e if control failed, then recovery".
                                       Now we force a recovery for "ESC" (see "else" statement), but we kept the piece of code to back... if necessary!*/
                                    if(event.data.nativeEvt.keyCode == keys["VK_TAB"]) {
                                        event.result = {"$act": requestActions.svcFormat};
                                        event.result.params = svcFmt.buildRqst(svcFmt.fmtSvc.toDisplay, currValue.v, fieldCxt.fmt, fieldInfo.type, fieldInfo.fieldIst);
                                        event.result.post = {"callback": function (succeeded, result, evtHandlingObj, initialEvt, callerFieldInfo) {
                                            var data;
                                            if (!succeeded) {
                                                data = callerFieldInfo.winModel.getDatasetValue(callerFieldInfo.$fieldMeta.$bind, null, false);
                                                callerFieldInfo.winModel.setData(callerFieldInfo.$fieldMeta.$bind, callerFieldInfo.type, null, data, {"noStoreCommmit": true});
                                            }
                                            if(initialEvt.data.nativeEvt.keyCode !== keys["VK_ESCAPE"]) {
                                                initialEvt.noControl = true;
                                                evtHandlingObj.controller.triggerAdx(initialEvt._name, initialEvt);
                                            }
                                        }, "args": [this, event, fieldInfo]};
                                    }
                                    else {
                                        oldValue = fieldInfo.winModel.getDatasetValue(fieldInfo.$fieldMeta.$bind, null, false);
                                        fieldInfo.winModel.removeFocus(fieldInfo.$fieldMeta.$bind);
                                        setTimeout(function() {
                                            fieldInfo.winModel.setData(fieldInfo.$fieldMeta.$bind, fieldInfo.type, null, oldValue, {"noStoreCommmit": true});
                                        }, 10);
                                    }
                                }
                            }
                        }
                        processed = true;
                    }
                    break;
                case keys["VK_ENTER"]:
                    if (($itemMeta = syraUtil.getMetaFromObject(event.target)) && sapUtil.isListScreen($itemMeta.$bind)) {
                        var grid = event.target.boxParent.boxParent;
                        var listIst = grid.getIst();
                        var filters = grid.getQuickSelValues();
                        if (this.sapController.hasFocus(listIst)) {
                            event.result = {
                                $act: requestActions.getSrvAction("SRV_BRWRAPIDESEL"),
                                params: {target: listIst, std: filters}
                            };
                        }
                        else {
                            event.result = {
                                $act: requestActions.getSrvActionEX("SUP_LIST_FILTER"),
                                params: {target: listIst, sudo: [null, filters]}
                            };
                        }
                        processed = true;
                    }
                    break;
            }
        }
        return processed;
    }
    
    function onDblClick(event){
        var processed = true;
        return processed;
    }
    
    function onClose(event){
        var processed = undefined;
        event.result = {"$act": requestActions.getSrvAction("SRV_ABANDON")};
        processed = true;
        return processed;
    }
    
    function onClick(event){
        var processed = undefined, $$field, currIst, $$cont;
        if (event.target && ($$field = $(event.data.nativeEvt.target))) {
            $$cont = $$field.closest("." + syraUtil.pageSectionK.body);
            if (($$cont && $$cont.length > 0) || _.find(syraUtil.pageSectionK, function(cssK){return $$field.hasClass(cssK);})) {
                currIst = this.sapController.getCurrInst(true);
                event.target.setFocus(null, currIst.xid, (currIst.nl || 0), (currIst.edit === null ? true : currIst.edit));
                processed = true;
            }
        }
        return processed;
    }
    
     function _getFieldInfo(field){
        var fieldInfo = {"readOnlyByModel": false, "isRadio": false, "isCheck": false, "isCombo": false, "isIco": false};
        if (field && (fieldInfo.$fusionPageMeta = syraUtil.getFusionPageMeta(field)) && (fieldInfo.$fieldMeta = syraUtil.getMetaFromObject(field)) && (fieldInfo.$fusionPageMeta.winModel.getProto())[fieldInfo.$fieldMeta.$bind]) {
            fieldInfo.fieldIst = sapUtil.makeIst(fieldInfo.$fusionPageMeta.winModel.getWinId(), fieldInfo.$fieldMeta.$bind);
            fieldInfo.readOnlyByModel = syraUtil.isFieldModelReadOnly(fieldInfo.$fusionPageMeta.winModel.getProto(), null, fieldInfo.$fieldMeta.$bind);
            fieldInfo.winModel = fieldInfo.$fusionPageMeta.winModel;
            fieldInfo.type = syraUtil.getFieldType(field);
            if ((fieldInfo.$field = field.$field)) {
                fieldInfo.isCheck = fieldInfo.$field.$type == syraUtil.dataTypes.booleanType ? true : false;
                if (!fieldInfo.isCheck && fieldInfo.$field.$type == syraUtil.dataTypes.choiceType) {
                    fieldInfo.isRadio = fieldInfo.$field.$format == syraUtil.wdgtTypes.radio ? true : false;
                    fieldInfo.isCombo = !fieldInfo.isRadio;
                }
                else {
                    fieldInfo.isIco = fieldInfo.$field.$type == syraUtil.dataTypes.iconType ? true : false;
                }
            }
        }
        return fieldInfo.$field ? fieldInfo : null;
    }
   
    function _toggleFieldAfterClick(reply, evtHandlingObj, $field, fieldIst, newVAlue, notifySrv){
        var fieldValue = evtHandlingObj.sapController.getEntityValueInReply(reply, fieldIst), value, chain = null;
        if (fieldValue == null || fieldValue === undefined) {
            // Ok, field value has not been updated by the server after focus/click, we can process post local update...
            value = util.duplicateObj(fieldIst);
            value.v = newVAlue;
            evtHandlingObj.sapController.forceValue(value, false, true);
            // ...and finally we inform the server by this change
            if(notifySrv) {
                chain = _getliveNotifyValueParams(fieldIst, null);
            }
        }
        return chain;
    }
    
    function _getliveNotifyValueParams(fieldIst, post){
        return {"$act": requestActions.getSrvAction("SRV_GETFOCUS"), "params": {"target": fieldIst}, "post": post};
    }
    
    function _openFieldCtx(reply, evtHandlingObj, initialEvent, field, fieldInfo){
        var ctxEx = [], lkEx = {}, ctx = evtHandlingObj.sapController.getTargetCtxMenuFromReply(reply);
        if (ctx && ctx.length > 0) {
            // Get items
             _.each(ctx, function(item, idx) {
                if(item[1]) {
                    this.push({"txt": item[2], "act": item[0], "id": "link" + idx});
                }
            }, ctxEx);
            // Set menu items
            evtHandlingObj.sapController.setCurrCtxMenu(ctxEx);
            sapUtil.appendMnus2Syra(lkEx, ctxEx, false);
            field.applyMetaData(lkEx);
            // Open Popup
            initialEvent.data.doEvent();
            fieldInfo.winModel.setCurrCtxPopup(field);
        }
        delete initialEvent.data.doEvent;
    }

    function _openDatePicker(succeeded, result, evtHandlingObj, initialEvent, field, callerFieldInfo){
        var session;
        if(evtHandlingObj.sapController.hasFocus(callerFieldInfo.fieldIst) && !syraUtil.getPopupDateFromField(field)) {
            if(succeeded) {
                initialEvent.data.doEvent(result ? {"internalValue": parseInt(svcFmt.getReplyResult(result), 10)} : null);
                callerFieldInfo.winModel.setCurrCtxPopup(field);
           }
            else {
                session = (evtHandlingObj.sapController.getSiteController()).getSession();
                callerFieldInfo.winModel._showFieldError(field, [session.getlabel("error", "22", "Date entered is invalid; calendar can not be opened")]);
                // TODO : a little message?
            }
        }
        delete initialEvent.data.doEvent;
    }

    function onFieldKeyDown(event){
        return onKeyDown.call(this, event);
    }

    function onFieldClick(event){
        var processed = undefined, fieldInfo;
        if (event.target && (fieldInfo = _getFieldInfo(event.target)) && !fieldInfo.isCheck) {
            // What should we do with this event?
            if (!fieldInfo.readOnlyByModel && !this.sapController.hasFocus(fieldInfo.fieldIst)) {
                event.result = {"$act": !fieldInfo.isIco ? requestActions.getSrvAction("SRV_GETFOCUS") : requestActions.getSrvActionEX("SUP_FIELD_ICOCLICK")};
                event.result.params = {"target": fieldInfo.fieldIst};
                if (event.data.doEvent) {
                    event.result.post = {"callback": event.data.doEvent, "args": []};
                }
                else if (fieldInfo.isRadio) {
                        event.result.post = {"exposeReply": true, "callback": _toggleFieldAfterClick, "args": [this, event.target, fieldInfo.fieldIst, event.target.getDataValue(), true]};
                }
                processed = true;
            }
            else if (!fieldInfo.readOnlyByModel) {
                if (fieldInfo.isRadio) {
                    event.result = _getliveNotifyValueParams(fieldInfo.fieldIst, {"exposeReply": true, "callback": _toggleFieldAfterClick, "args": [this, event.target, fieldInfo.fieldIst, event.target.getDataValue(), false]});
                    processed = true;
                }
                else if (fieldInfo.isIco) {
                    event.result = {"$act": requestActions.getSrvAction("SRV_KIB")};
                    event.result.params = {"target": fieldInfo.fieldIst};
                    processed = true;
                }
            }
            if (fieldInfo.readOnlyByModel) {
                this.sapController.setLocalIst(fieldInfo.fieldIst);
            }
        }
        else {
            // Let it be!
        }
        return processed;
    }
    
    function onDatePickerClick(event){
        var processed = undefined, fieldInfo, currValue, fieldCxt, self = this;
        if (event.target && (fieldInfo = _getFieldInfo(event.target))) {
           if (!fieldInfo.readOnlyByModel && !this.sapController.hasFocus(fieldInfo.fieldIst)) {
                // Request focus
                event.result = {"$act": requestActions.getSrvAction("SRV_GETFOCUS")};
                event.result.params = {"target": fieldInfo.fieldIst};
                event.result.post = {"callback": function (evtHandlingObj, initialEvent, callerField) {
                        var fieldContext = evtHandlingObj.sapController.getCurrInst();
                        _openDatePicker(true, fieldContext.raw ? svcFmt.formReply(fieldContext.raw, fieldContext.fmt) : null, evtHandlingObj, initialEvent, callerField, fieldInfo);
                    }, "args": [this, event, event.target, fieldInfo]};
                processed = true;
            }
            else if (!fieldInfo.readOnlyByModel && !syraUtil.getPopupDateFromField(event.target)) {
                // Open picker
                fieldCxt = this.sapController.getCurrInst();
                currValue = fieldInfo.winModel.getValue(fieldInfo.fieldIst.xid, fieldInfo.fieldIst.nl);
                if (currValue.v)
                {
                    event.result = {"$act": requestActions.svcFormat};
                    event.result.params = svcFmt.buildRqst(svcFmt.fmtSvc.toRaw, currValue.v, fieldCxt.fmt, fieldInfo.type, fieldInfo.fieldIst);
                    event.result.post = {"callback": _openDatePicker, "args": [this, event, event.target, fieldInfo]};
                    processed = true;
                }
                else {
                     setTimeout(function() {
                        _openDatePicker(true, null, self, event, event.target, fieldInfo);
                    }, 10);
                    processed = true;
                }
            }
            if (fieldInfo.readOnlyByModel && !syraUtil.getPopupDateFromField(event.target)) {
                // Nothing... sorry!
                this.sapController.setLocalIst(fieldInfo.fieldIst);
                processed = true;
            }
        }
        else {
            // Let it be!
        }
        return processed;
    }

    function onDatePickerClose(event){
        var processed = undefined, fieldInfo, fieldCxt;
        if (event.target && (fieldInfo = _getFieldInfo(event.target)) && this.sapController.hasFocus(fieldInfo.fieldIst)) {
            fieldCxt = this.sapController.getCurrInst();
            event.result = {"$act": requestActions.svcFormat};
            event.result.params = svcFmt.buildRqst(svcFmt.fmtSvc.rawToEdit, event.data.internalValue, fieldCxt.fmt, fieldInfo.type, fieldInfo.fieldIst);
            event.result.post = {"callback": function (succeeded, reply, evtHandlingObj, initialEvent, callerField, callerFieldInfo) {
                    if(evtHandlingObj.sapController.hasFocus(callerFieldInfo.fieldIst)) {
                        fieldInfo.winModel.setCurrCtxPopup(null);
                        initialEvent.data.doStatements({"value": svcFmt.getReplyResult(reply)});
                        delete initialEvent.doStatements;
                    }
                }, "args": [this, event, event.target, fieldInfo]};
            processed = true;
        }
        return processed;
    }


    function onFieldClickPicker(event){
        var processed = undefined, pickerType, fieldInfo = null, isCtx = false, params = null, act = null, actEx, popupCtx;
        if (event.data) {
            // Init
            if ((pickerType = event.data.pickerType)) {
                switch (pickerType) {
                    case "contextMenu":
                        params = (fieldInfo = _getFieldInfo(event.target)) ? {"target": fieldInfo.fieldIst} : null;
                        isCtx = true;
                        popupCtx = syraUtil.getPopupCtxFromField(event.target);
                        if(fieldInfo && !popupCtx) {
                            // Ctx popup is not already displayed
                            act = "SRV_OPEN_CTXMENU";
                            actEx = "SUP_FIELD_CTX";
                        }
                        else if (popupCtx && event.data.doEvent) {
                            // Ctx popup is already displayed : force close popup
                            event.data.doEvent();
                            delete event.data.doEvent;
                        }
                        break;
                    case "tunnel":
                        act = "SRV_TUNNEL";
                        actEx = "SUP_FIELD_DRILL";
                        break;
                    case "lookup":
                        act = "SRV_BOITE_SELECTION";
                        actEx = "SUP_FIELD_SELECT";
                        break;
                    case "date":
                        return onDatePickerClick.call(this, event);
                        break;
                    default:
                        // Field own picker, like  "choice arrow", "numeric-up", "numeric-down", etc...
                        return onFieldClick.call(this, event);
                        break;
                }
            }
            fieldInfo = fieldInfo || _getFieldInfo(event.target);
            // Set action...
            if (act && fieldInfo && !fieldInfo.readOnlyByModel) {
                if (this.sapController.hasFocus(fieldInfo.fieldIst)) {
                    event.result = {"$act": requestActions.getSrvAction(act)};
                    event.result.params = params;
                }
                else {
                    event.result = {"$act": requestActions.getSrvActionEX(actEx)};
                    event.result.params = {"target": fieldInfo.fieldIst};
                }
                if (event.data.doEvent && isCtx) {
                    event.result.post = {"exposeReply": isCtx, "callback": isCtx ? _openFieldCtx : event.data.doEvent, "args": isCtx ? [this, event, event.target, fieldInfo] : []};
                }
                processed = true;
            }
        }
        else {
            // Let it be!
        }
        return processed;
    }
        
    function onFieldChange(event){
        var processed = undefined, fieldInfo, hasFocus;
        if (event.target && (fieldInfo = _getFieldInfo(event.target))) {
            // What should we do with this event?
            hasFocus = this.sapController.hasFocus(fieldInfo.fieldIst);
            if ((fieldInfo.isCombo || fieldInfo.isCheck) && hasFocus) {
                event.result = _getliveNotifyValueParams(fieldInfo.fieldIst, null);
                processed = true;
            }
            else if(fieldInfo.isCheck && !hasFocus) {
                event.result = _getliveNotifyValueParams(fieldInfo.fieldIst, {"exposeReply": true, "callback": _toggleFieldAfterClick, "args": [this, event.target, fieldInfo.fieldIst, event.target.getDataValue(), true]});
                processed = true;
            }
        }
        else {
            // Let it be!
        }
        return processed;
    }
};
