	_initNewCurrIst: function(params) {
		var isSameIst, editVal, currIst, oldShow, oldFmt, oldIst, field;
		// Check border line cases...
		if (this._checkEvilInstance(params)) {
			return true;
		}
		// Check old field context
		currIst = this._currCtx.ist;
		oldIst = util.duplicateObj(currIst);
		isSameIst = sapUtil.cmpIst(params, currIst, true);
		if (!params.ctrlFailed) {
			this._wndwsStack.setDirty(currIst.win, currIst.xid, currIst.nl, false);
		}
		if (sapUtil.isFieldInst(currIst.xid) && currIst.edit && (!isSameIst || (!sapUtil.isGridInst(currIst) && !params.ctrlFailed))) {
			// Set "show" value in prev field, if ctrl OK (little trick to avoid erase field if same ist in  cell array...)
			oldFmt = this._wndwsStack.getDataFmt(currIst.win, currIst.xid, currIst.nl);
			if (oldFmt) {
				oldShow = this._wndwsStack.getDataStoreValue(currIst.win, currIst.xid, currIst.nl, false, true);
				this._wndwsStack.setDataStoreValue(currIst.win, currIst.xid, currIst.nl, oldShow, {
					"noStoreCommmit": true,
					"discardUnsort": true
				});
			}
		}
		// Init new context
		_.each(this._currCtx, function(value, key) {
			delete this[key];
		}, this._currCtx);
		this._currCtx = {
			"ist": {}
		};
		_.each(params, function(value, name) {
			if (name != "acts") {
				this[name] = value;
			}
		}, this._currCtx.ist);
		this._currCtx.localIst = null;
		currIst = this._currCtx.ist;
		if (sapUtil.isFieldInst(currIst.xid)) {
			// Set raw and format in current field ctx
			currIst.fmt = this._wndwsStack.getDataFmt(params.win, params.xid, params.nl);
			currIst.raw = this._wndwsStack.getDataStoreValue(params.win, params.xid, params.nl, false, true, {
				"rawVal": true
			});
			if (!isSameIst && currIst.fmt && currIst.raw) {
				// Put edit value in field
				editVal = this.fmtWrapper.rawToEdit(currIst.raw, currIst.fmt);
				if (editVal.succeeded) {
					this._wndwsStack.setDataStoreValue(currIst.win, currIst.xid, currIst.nl, editVal.edit, {
						"formatted": true,
						"noStoreCommmit": true,
						"discardUnsort": true
					});
				}
			}
		}
		// Misc controls...
		sapUtil.setFieldMod(this._wndwsStack.getBoundField(currIst.win, currIst.xid), currIst);
		if (!isSameIst) {
			if (sapUtil.isGridInst(oldIst)) {
				// Check grid cardview status...
				sapUtil.checkPopupCard(oldIst, params, this._wndwsStack.getBoundField(oldIst.win, sapUtil.getBlockInst(oldIst.xid)));
			}
		}
		return false;
	},
