	_addCoreSubMenuToRoot: function(fid, id, rank){
		if(!this._coreMenus[fid].items) {
			this._coreMenus[fid].items = {};
		}
 		this._coreMenus[fid].items[id] = this._coreMenus[id];
 		this._coreMenus[fid].items[id].rnk = rank;	   		
 		this._coreMenus[fid].items[id].typ = this._coreMenus[id].typ || 0;
	},

	_getMenuItem: function (fid, id, txt, txtNum, typ, rnk, act, actTxt, inactive, opts, linkId){
    	return {"fid": fid, "id": id.toString(), "txt": txt || this._session.getlabel("text", txtNum, txtNum), "typ": typ, "rnk": rnk, "act": act || requestActions.getSrvAction(actTxt), "inactive": inactive, "linkId": linkId, "opts": (opts ? util.duplicateObj(opts) : undefined)};
    },

	addMnuBarToProto: function(winId, winFacet, winMenusDef, winProto){
 		var i, j, len, linksArray, layout, timer = utilBis.getTimer(true), bar, nbLayoutItems, mainGrpInProto, mainBtnId;
 		// Init
		syraUtil.appendProto(winProto, {"$article": {"$menus": {"$layout": {"$items" : []}}}});
		layout = winProto.$article.$menus.$layout;
		nbLayoutItems = layout.$items.length;
		mainGrpInProto = !!(nbLayoutItems > 0 && layout.$items[0].$style && layout.$items[0].$style === _kMnuHelper.mainSty);
 		// Get win bar def
 		bar = this._getWinMenuBar(winId, winFacet, winMenusDef, winProto, !mainGrpInProto)
		// Set menu layout, consider that items set in proto are members of "ROOT_SUPERVISOR", so the last group in the bar
		if(nbLayoutItems === 0) {
			layout.$items = bar.$menus.$items;
		}
		else {
			j = mainGrpInProto ? 1 : 0;
			i = 0;
			while(bar.$menus.$items[i].rnk <= _insertRank.protoMnus) {
				layout.$items.splice(i + j, 0, bar.$menus.$items[i++]);
			}
			for(j = i, len = bar.$menus.$items.length; j < len; j++) {
				layout.$items.push(bar.$menus.$items[j]);
			}
		}
		// Add "main" Button... i.e "New" at moment
		if(layout.$items.length > 0 && winFacet != syraUtil.pageFacet.modal3) {
			i = 0;
			while(layout.$items[i].$style && layout.$items[i].$style !== _kMnuHelper.mainSty) {
				i++;
			}
			i = i < layout.$items.length ? i : 0;
			if(layout.$items[i].$layout && layout.$items[i].$layout.$items) {
				mainBtnId = requestActions.getSrvAction("SRV_FICHIER_NOUVEAU");
				layout.$items[i].$layout.$items.splice(0, 0, {"$bind": mainBtnId, "$style": _kMnuHelper.mainSty});
				bar.$links[mainBtnId] = {"$title": this._session.getlabel("text", "14", "14"), "$act": mainBtnId};
			}
		}
		// Set links
		if(winProto.$links) {
			linksArray = Object.keys(winProto.$links);
			for(i = 0, len = linksArray.length; i < len; i++ ) {
				bar.$links[linksArray[i]] = util.duplicateObj(winProto.$links[linksArray[i]]);
			}
			delete winProto.$links;
		}
		winProto.$links = bar.$links;
        this._session.logger.timer(null, "Add Menu Bar Definition : total [" + timer.elapsedMs() + "]");
		//this._session.logger.trace({"toJson": true}, "Menu Bar Syracuse : ", bar);
	},

	releaseWinMenu: function(winId){
		var winMenu;
		if((winMenu = this.winMenuColl[winId])) {
			if(winMenu.winProto) {
		 		delete winMenu.winProto.$article.$menus;
		 		delete winMenu.winProto.$links;
		 	}
			winMenu.dispose();
			delete this.winMenuColl[winId];
		}	
	},

	_getWinMenuBar: function(winId, winFacet, winMenusDef, winProto, setMainGrp){
		if(this.winMenuColl[winId]) {
			this.releaseWinMenu(winId);
		}
		(this.winMenuColl[winId] = new WinMenu()).initialize(this, winMenusDef, this._coreMenus, winProto, winFacet, setMainGrp, this._mnuWithMagneto, this._session);
		this.winMenuColl[winId].buildMenus();
		//this._session.logger.trace({"toJson": true}, "Menu Bar JSON : ", this.winMenuColl[winId]._menuBarTree);
		return this.winMenuColl[winId].getMenuBar();
	},

	addStatusIco: function(winId, syraUISheet, icoId, act){
		var ret = true, isMnuStatus = _kMnuWithStatus.act[act], newItem = {"$links": {}};
		if(this.winMenuColl[winId] && syraUISheet) {
			newItem.$links[act] = {"$title": "" + act, "$act": act};
			_kMnuHelper.appendIco({"typ": "4", "opts": {"icoVp": "x3ico", "ico": icoId}}, newItem.$links[act]);
			syraUISheet.addMenuItem(newItem, _menuIDs.STATUS_CMN);
		}
		return ret;
	},

	showStatusIco: function(winId, syraUISheet, act, show){
		var ret = true, updateItem = {"$links": {}};
		if(this.winMenuColl[winId]) {
			updateItem.$links[act] = {"$isHidden": !show};
			syraUISheet.applyChange(updateItem);
		}
		return ret;
	},
	
    dispose: function(){
  		_.each(this.winMenuColl, function(ptr, key){this.releaseWinMenu(key);}, this);
  		_.each(this._tabMenusFather, function(ptr, key){delete this[key];}, this._tabMenusFather);
   		_releaseMenusObj(this._coreMenus);
    	if(this._session) {
    		delete this._session;
    	}
    },

