		// Common for all types of window
		ids[_menuIDs.FILE_EX] = {"tag":"FILE_EX", "txt": this._session.getlabel("text", 2, "File"), "typ": 0, "opts": {"collapsed": true}, "items": {}};
		ids[_menuIDs.SUBMENU_PRINT_CMN] = {"tag":"SUBMENU_PRINT_CMN", "txt": this._session.getlabel("text", 3, "Print"), "typ": 0, "opts": {"collapsed": false, "ico": "6", "icoVp": "x3ico", "layout": "row"}};
		ids[_menuIDs.HELP_EX] = {"tag":"HELP_EX", "txt": this._session.getlabel("text", 4, "Help"), "typ": 0, "opts": {"collapsed": true}};
