"use strict";

exports.getFieldValue = function(options) {
	var values = ["", options.field.getInputValue().replace(/\**$/, ""), ""];
	switch (options.operator) {
		case "none":
			break;
		case "gt":
			values[0] = ">";
			break;
		case "ge":
			values[0] = ">=";
			break;
		case "lt":
			values[0] = "<";
			break;
		case "le":
			values[0] = "<=";
			break;
		case "eq":
			values[0] = "=";
			break;
		case "ne":
			values[0] = "<>";
			break;
		case "like_s":
			values[2] = "*";
			break;
		case "like":
			values[0] = "*";
			values[2] = "*";
			break;
		case "between":
			break;
	}
	return values.join("");
};

exports.getFieldOperators = function($type, $referenceType) {
	switch ($referenceType || $type) {
		case "application/x-date":
		case "application/x-time":
		case "application/x-datetime":
			return ["none", "ge", "gt", "lt", "le", "eq", "ne"];
		case "application/x-string":
			return ["none", "like_s", "like", "gt", "ge", "lt", "le", "eq", "ne"];
		case "application/x-choice":
		case "application/x-real":
		case "application/x-decimal":
		case "application/x-integer":
		case "application/x-quantity":
			return ["none", "eq", "gt", "ge", "lt", "le", "ne"];
	}
	return ["none", "eq", "ne"];
};