"use strict";
var helpers = require('syracuse-core/lib/helpers');
var DesktopList = require("syracuse-ui/lib/field/array/list").DesktopList;
//-----------------------------------------------
// GridSyra
//-----------------------------------------------
function Grid(){

}

exports.Grid = helpers.defineClass(Grid, DesktopList, {
	setFocus: function(){
        var record = this._store._records[arguments[1] - 1], field;
        if (record) {
            field = record.boundFields[arguments[0]];
            if (field) {
                field[0].focus();
            }
        }
	},
    getDataValue: function(){
        var record = this._store._records[arguments[1] - 1], field;
        if (record) {
            field = record.boundFields[arguments[0]];
            if (field && field[0].$isEditMode) {
                return field[0].getDataValue();
            }
        };
        return null;
    },
	
    resetFocus: function(){
		var record = this._store._records[arguments[1] - 1], field;
		if (record) {
			field = record.boundFields[arguments[0]];
			if (field) {
			
			}
		}
	},
	getSelectedRecordsIndex:function(){
		var result=[],index;
		for (index in this._store._records){
			if(this._store._records[index]._selectorCheck && this._store._records[index]._selectorCheck.checked){
				result.push(parseInt(index,10))
			}
		}
		return result;
	}
});
