var helpers = require('syracuse-core/lib/helpers');
var Field = require('syracuse-ui/lib/common/field/field').Field;

function FusionIconField(){
}

exports.FusionIconField = helpers.defineClassEx(FusionIconField,Field, {
    render: function(){
        if (this.$item.$inplace) {
            this.$$item.css("text-align", "center");
        }
        if (this.$isEditMode) {
			
        }
        else {
            
        }
    },
	getDataValue: function(){
		return this.currentValue;
    },
	setDataValue: function(value){
		this.currentValue=value;
		var v=parseInt(value,10);
		if(isNaN(v) || v==-1){
			this.$$dataValue.toggleClass("s-fusion-icon", false)
		}else{
			this.$$dataValue.toggleClass("s-fusion-icon", true);
			this.$$dataValue.css("background-position",(v)*(-16)+"px");
		}
    }
});