var locale = require('syracuse-core/lib/locale');

exports.startService = function($url){
    var reqOpt = {};
    reqOpt.noDisplayErr = true;
    reqOpt.method = "GET";
    reqOpt.$location = {
        $url: "http://localhost:8126/sdata/sprint1/settings/sprint1/countries%28%274151c62c-2ce4-4e5e-9129-a97f67280996%27%29?representation=country2.$details&role=au&trackngId=1"
    };
    document.controller.sendRequest(null, reqOpt, onSuccess, onError);
}

function onSuccess(data, response, $url){
    console.log("data:\n" + JSON.stringify(data) + "\n");
    console.log("response:\n" + JSON.stringify(response) + "\n");
    console.log("$url:\n" + JSON.stringify($url) + "\n");
}

function onError(error, httpquery){
    console.log("error:\n" + JSON.stringify(error) + "\n");
    console.log("httpquery:\n" + JSON.stringify(httpquery) + "\n");
}

