"use strict";
var _helpers = require('syracuse-core').helpers;
var _select = require("./select");

function RoleList() {}

exports.RoleList = _helpers.defineClass(RoleList, _select.List, {
	load: function(page) {
		this.level = "rolePages";
		this.RecordClass = RoleRecord;
		_select.List.prototype.load.call(this, page);
		this.addChildren();
	},
	setHeight: function(height) {
		this.slot.style.height = height + "px";
		this.resize(height);
	},
	ensureNoChild: function() {
		syra_dom.hide(this.slot, this.children.length == 0);
	}
});

function RoleRecord() {}

_helpers.defineClass(RoleRecord, _select.Record, {
	addContentActions: function() {
		this.addRefresh();
	}
});