"use strict";
exports.click = require('./clickHandlers');
var _quickEditBuilder = require("syracuse-ui/lib/field/array/gridMode/tools/quickEditBuilder");
var _paging = require("syracuse-ui/lib/field/array/tools/paging");
var _fontIcons = require("syracuse-ui/lib/menus/fontIcons").map;

exports.fontIcons = _fontIcons;
var _cssFontIconBtn = " s-fonticon-btn ";
var _index = 0;
var _buttons = {};
if (document.dir === 'rtl') {
	_cssFontIconBtn += " s-rtl-img ";
}

exports.btnWidth = 20;

exports.getDocumentIcon = function(mediaType) {
	mediaType = mediaType.toLowerCase();
	if (mediaType.indexOf("image") >= 0) {
		return "image";
	}
	if (mediaType.indexOf("pdf") >= 0) {
		return "pdf";
	}
	if (mediaType.indexOf("excel") >= 0 || mediaType.indexOf("officedocument.spreadsheet") >= 0) {
		return "excel";
	}
	if (mediaType.indexOf("mailmerge") >= 0) {
		return "mailmerge";
	}
	if (mediaType.indexOf("word") >= 0) {
		return "msword";
	}
	if (mediaType.indexOf("ms-powerpoint") >= 0 || mediaType.indexOf("officedocument.presentationml") >= 0 || mediaType.indexOf("ppt") >= 0) {
		return "powerpoint";
	}
	if (mediaType.indexOf("xml") >= 0) {
		return "xml";
	}
	if (mediaType.indexOf("html") >= 0) {
		return "html";
	}
	if (mediaType.indexOf("json") >= 0) {
		return "json";
	}
	if (mediaType.indexOf("text") >= 0) {
		return "text";
	}
	mediaType = mediaType.replace("application/", "");
	return (mediaType.indexOf("image") >= 0) ? "image" : mediaType.replace("/", "-");
};

exports.setMenuItemTitle = function(menu, $title, $iconValue) {
	menu.$title = ($title || "") + ""; //ensure title is string
	delete menu.domItem.syraTitle;
	var title = menu.getTitle();
	menu.domItem.title = menu.$description || title;

	if (!menu.$item.$noText && !(menu.$item.$icon && menu.$item.$icon.$mode == "icon")) {
		if (title.indexOf("<em") >= 0) {
			menu.domItem.innerHTML = title.replace(/<em>/g, "<em class='" + menu.$skin + "-em'>");
		} else {
			menu.domItem.textContent = title;
		}
	}
	if (menu.$item.$icon) {

		if ($iconValue != undefined) {
			menu.$iconValue = $iconValue;
		}
		var name = menu.$iconValue || menu.value || menu.$item.$icon.$value;
		if (!name) {
			name = menu.$sourceBind || menu.$item.$bind;
		}
		name = (name[0] == "$") ? name.substr(1) : name;
		var fontIcon = syra_menus.fontIcons[name];

		if (menu.$item.$icon && !menu.iconValue) {
			menu.cssTitle = null;
			var cssBtn = _cssFontIconBtn + "s-btn-" + name;
			if (menu.$item.$icon.$mode == "icon") {
				menu.$item.$noText = true;
				menu.iconValue = menu.domItem;
				menu.cssTitle = fontIcon ? cssBtn : " s-mn-icon";
			} else {
				var iconCss = menu.$skin + "-icon s-mn-icontext" + (fontIcon ? cssBtn : " s-mn-icon");
				if (menu.$item.$css) {
					iconCss += " " + menu.$item.$css;
				}
				menu.iconValue = syra_dom.addDiv(iconCss);
				menu.cssTitle = " s-mn-mode-icontext";
			}
			menu.cssUpdated = true;
		}
		if (fontIcon) {
			menu.iconValue.innerHTML = fontIcon;
		} else {
			var $path = syra_site.$iconPath + (menu.$item.$icon.$path || "");
			menu.iconValue.style.backgroundImage = "url('" + $path + name + ".png')";
		}
		if (menu.iconValue != menu.domItem) {
			menu.domItem.insertBefore(menu.iconValue, menu.domItem.firstChild);
		}
	}

	if (menu.$description && menu.$item.$isDescriptionVisible) {
		if (!menu.descriptionItem) {
			menu.descriptionItem = syra_dom.addDiv(menu.$item.$skinDescription || (menu.$skin + "-desc"));
		}
		menu.descriptionItem.textContent = menu.$description;
		menu.domItem.appendChild(menu.descriptionItem);
	}
	if (!menu.isExternalHidden && menu.$target == "blank") {
		if (!menu.external) {
			menu.external = document.createElement("div");
			exports.setFontIconButton(menu.external, "external");
		}
		menu.domItem.appendChild(menu.external);
	}
};

function _onPickerClick(event) {
	var self = this;
	if (!self.parent.authoringNode && !self.parent.$isDisabled) {
		self.parent.page.externalAdapter.onFieldClickPicker({
			field: self.parent,
			pickerType: self.pickerId,
			doEvent: function(options) {
				self.pickerclick(options);
			}
		});
	}
}

exports.button = {
	getButton: function(id) {
		return _buttons[id];
	},
	addFieldPicker: function(btn) {
		btn.css = btn.parent.$mnPickersCss;
		if (btn.parent.$item.$css) {
			btn.css += " " + btn.parent.$item.$css;
		}
		btn.fontIcon = btn.fontIcon || btn.pickerId;
		btn.isHidden = btn.parent.$isDisabled || btn.parent.$isReadOnly;
		this.add(btn);
		btn.parent.onAddMenuPicker && btn.parent.onAddMenuPicker(btn);
		return btn;
	},
	highlightFieldPickers: function(field, on) {
		if (field && field.buttons) {
			for (var ii = 0, jj = field.buttons.length; ii < jj; ii++) {
				var btn = field.buttons[ii];
				btn.pickerId && syra_dom.toggleClass(btn.link, "s-field-enter", on);
			}
		}
	},
	add: function(btn) {
		if (btn.pickerId) {
			btn.iconOnly = true;
			btn.pickerclick = btn.btnclick;
			btn.btnclick = _onPickerClick;
			btn.parent["picker_" + btn.pickerId] = btn;
		}
		btn.id = ++_index;
		if (!btn.link) {
			if (btn.isIndicator) {
				btn.link = document.createElement("div");
			} else {
				btn.link = document.createElement("a");
				if (!btn.pickerId) {
					btn.link.setAttribute("href", "#");
				}
			}
		}
		var link = btn.link;
		if (btn.btnclick) {
			link.syraBtnclick = btn.id;
		}
		_buttons[link.syraBtn = btn.id] = btn;
		if (btn.shortCutTip) {
			link.syraShortCutTip = btn.shortCutTip;
		}
		if (btn.text === undefined) {
			btn.text = "";
		}
		if (btn.iconOnly) {
			link.title = btn.text;
			link.syraTip = 2;
			link.syraIconCss = btn.css;
			if (!(btn.fontIcon && exports.setFontIconButton(link, btn.fontIcon))) {
				link.className = btn.css;
				if (btn.imageName) {
					link.className += " s-mn-icon";
					link.style.backgroundImage = "url('" + syra_site.$iconPath + btn.imageName + "')";
				}
			}
		} else {
			link.className = btn.css;
			link.syraTip = 1;
			link.title = btn.title || btn.text;
			if (btn.titleImage) {
				btn.titleImageItem = syra_dom.addDiv(btn.css + "-title-icon");
				btn.titleImageItem.style.backgroundImage = "url('" + syra_site.$iconPath + btn.titleImage + "')";
				link.appendChild(btn.titleImageItem);
			} else {
				if (btn.isTextHTML) {
					link.innerHTML = syra_dom.textToHTML(btn.text);
				} else {
					link.textContent = btn.text;
				}
			}
			if (btn.fontIcon || btn.imageName) {
				btn.iconflag = document.createElement("div");
				if (btn.fontIcon) {
					btn.iconflag.syraIconCss = btn.css + "-flag s-mn-icontext";
					exports.setFontIconButton(btn.iconflag, btn.fontIcon);
				} else {
					btn.iconflag.className = btn.css + "-img s-mn-icontext";
					btn.iconflag.style.backgroundImage = "url('" + syra_site.$iconPath + btn.imageName + "')";
				}
				link.insertBefore(btn.iconflag, link.firstChild);
			}
		}
		btn.isDisabled && this.disable(btn, true);
		btn.isHidden && this.hide(btn, true);
		btn.slot && btn.slot.appendChild(link);
		(btn.parent.buttons = btn.parent.buttons || []).push(btn);
		return btn;
	},
	setText: function(btn, text, icon) {
		btn.iconflag && btn.link.removeChild(btn.iconflag);
		if (text != undefined) {
			btn.text = text;
			if (btn.link.syraTipTitle) {
				btn.link.syraTipTitle = text;
			} else {
				btn.link.title = text;
			}
			if (!btn.iconOnly && !btn.titleImage) {
				if (btn.isTextHTML) {
					btn.link.innerHTML = syra_dom.textToHTML(btn.text);
				} else {
					btn.link.textContent = btn.text;
				}
			}
		}
		btn.iconflag && btn.link.insertBefore(btn.iconflag, btn.link.firstChild);
		if (icon) {
			if (btn.imageName) {
				btn.imageName = icon;
				(btn.iconflag || btn.link).style.backgroundImage = "url('" + syra_site.$iconPath + btn.imageName + "')";
			} else {
				exports.setFontIconButton(btn.iconflag || btn.link, icon);
			}
		}
		if (btn.titleImage) {
			btn.link.appendChild(btn.titleImageItem);
		}
	},
	hide: function(btn, hidden) {
		if (btn) {
			if (btn.link_isHidden != hidden) {
				btn.link_isHidden = hidden;
				syra_dom.hide(btn.link, hidden);
			}
		}

	},
	visibility: function(btn, visible) {
		if (btn) {
			visible = visible ? "visible" : "hidden";
			if (btn.link.style.visibility != visible) {
				btn.link.style.visibility = visible;
			}
		}
	},
	disable: function(btn, disabled) {
		btn && syra_dom.disableItem(btn.link, btn.$isDisabled = disabled);
	},
	remove: function(btn) {
		if (btn) {
			if (btn.pickerId) {
				delete btn.parent["picker_" + btn.pickerId];
			}
			syra_dom.removeChild(btn.link);
			this.dispose(btn);
		}
	},
	onItemInOut: function(syraBtn, on) {
		var btn = _buttons[syraBtn];
		if (btn && !btn.$isDisabled && !btn.parent.$isDisabled) {
			syra_dom.toggleClass(btn.link, "s-enter", on);
			if (on) {
				var showAlways = btn.iconOnly;
				syra_tip.titleTip.show(btn.link.title, btn.link, !showAlways); // if no show always => check tip visibility
			}
		}
	},
	dispose: function(btn) {
		if (btn) {
			delete _buttons[btn.id];
		}
	}
};

exports.setFontIconButton = function(btn, fontIcon) {
	if (fontIcon[0] == "$") {
		fontIcon = fontIcon.substr(1);
	}
	var icon = _fontIcons[fontIcon];
	if (icon) {
		btn.innerHTML = icon;
		btn.className = btn.syraIconCss + _cssFontIconBtn + "s-btn-" + fontIcon;
		return true;
	}
};

exports.onItemInOut = function(onEnter, event, target) {
	if (onEnter) {
		if (target.outOfDate) {
			target.setMenu(null, target.articleParent.dataset, target.outOfDate);
			target.outOfDate = false;
		}

		var showAlways;
		if (target.domItem) {
			showAlways = target.domItem.syraTitle && ((target.domItem.title && target.domItem.title != target.domItem.syraTitle) || target.$item.$noText);
			target = target.domItem;
		}
		syra_tip.titleTip.show(target.title, target, !showAlways); // if no show always => check tip visibility
	}
	target = target.domItem ? target.domItem : target;
};



var _listMenuBinds = ["$edit", "$create", "$delete", "$details", "$design"];

exports.pickers = {
	menus: {
		add: function(field) {
			return syra_menus.button.addFieldPicker({
				parent: field,
				pickerId: "menus",
				fontIcon: "menus",
				text: syra_local.flActions,
				shortCutTip: (field.isField && !field.isArrayField) ? syra_shortCuts.tip.contextMenu : null,
				btnclick: this.onClick
			});
		},
		onClick: function() {
			exports.pickers.menus.showPopup(this.parent, this);
		},
		show: function(field) {
			if (field.picker_menus) {
				var show = field.$isEditMode || field.hasValue();
				exports.button.hide(field.picker_menus, !show);
				if (!field.picker_menus.link.parentNode && field.onAddMenuPicker) {
					field.onAddMenuPicker(field.picker_menus);
				}
			}
		},
		showPopup: function(field, picker, isChart) {
			if (field) {
				if (!field.menusPopup) {
					var $item = {
						$noText: true,
						$title: syra_local.fieldActions,
						$clientId: field.id + "-menu-picker",
						$skin: "s-mn-popup",
						$itemSkin: "s-mn-link",
						$category: "menus",
						$layout: {
							$items: []
						}
					};
					var article = (field.isDesktopPage || field.isRecordArticle) ? field : field.articleParent;
					if (field.menusBox && article.menuItems) {
						//clear previous popup menus 
						var items = field.menusBox.boxChildItems;
						for (var ii = 0, jj = items.length; ii < jj; ii++) {
							delete article.menuItems[items[ii].$bind];
							article.removeItem(items[ii]);
						}
						article.removeItem(field.menusBox, true, true);
					}
					field.menusBox = field.page.loadNewItem(null, $item, article);
					field.menusBox.contextField = field;
					var binds = Object.keys(field.$menusPopup);
					//setOrder
					var newBinds = [];
					for (var ii = 0, jj = _listMenuBinds.length; ii < jj; ii++) {
						var found = binds.indexOf(_listMenuBinds[ii]);
						if (found >= 0) {
							binds.splice(found, 1);
							newBinds.push(_listMenuBinds[ii]);
						}
					}
					binds = newBinds.concat(binds);
					for (var ii = 0, jj = binds.length; ii < jj; ii++) {
						var $menu = field.$menusPopup[binds[ii]];
						if (!$menu.$icon) {
							if (_listMenuBinds.indexOf($menu.$sourceBind) >= 0) {
								$menu.$icon = {
									$mode: "iconText",
									$path: field.$iconPath
								};
							}
						}
						var menuItem = exports.group.addMenuItem(field.menusBox, $menu);
						article.$menus[$menu.$bind] = $menu;
						menuItem.setMenu($menu, article.dataset);
					}
					exports.menus._disableItems(field, field.menuItems);
					var btnLink;
					if (isChart) {
						if (picker.className && picker.className.indexOf) {
							//prevent from svg picker
							syra_dom.toggleClass(picker, "s-open", true);
						}
						btnLink = picker;
					} else {
						exports.button.setText(picker, undefined, "menus_open");
						btnLink = picker.link;
					}
					syra_quality.openPopupMenu(field.menusBox);
					syra_site.activePopupMenu = field.menusPopup = syra_dlg.openPopup(field, {
						content: field.menusBox,
						slot: field.menusBox.body,
						picker: btnLink,
						$isAutoCloseTimeoutDisabled: true,
						position: {
							my: "right top",
							at: "right bottom",
							of: $(btnLink)
						},
						onClose: function() {
							if (field.menusPopup == syra_site.activePopupMenu) {
								delete syra_site.activePopupMenu;
							}
							if (isChart) {
								if (picker.className && picker.className.indexOf) {
									//prevent from svg picker
									syra_dom.toggleClass(picker, "s-open", false);
								}
							} else {
								exports.button.setText(picker, undefined, "menus");
							}
							field.menusPopup = null;
						}
					});
				} else {
					field.menusPopup.close();
				}
			}
		}
	},
	$localize: {
		add: function(field, $menu) {
			if (field.$item.$useLocalizePicker) {
				$menu.$isHidden = true;
			}
			if (field.$isEditMode && !field.picker_localize && field.$item.$useLocalizePicker) {
				syra_menus.button.addFieldPicker({
					parent: field,
					pickerId: "$localize",
					fontIcon: "localize",
					text: field.$menus.$localize.$title,
					btnclick: this.onClick
				});
			}
			$menu.$isPopup = true;
		},
		onClick: function() {
			syra_localizer.onMenuClick(this.parent, this.parent.$menus.$localize);
		}
	},
	$tunnel: {
		add: function(field, $menu) {
			$menu.$title = syra_local.fieldTunnel;
			$menu.$isPopup = syra_site.isTabletDevice || (field.$isEditMode && field.$item.$inplace && !field.$item.$isFilterMode);
			if (!$menu.$isPopup && !field.picker_$tunnel && !field.$item.$inplace) {
				syra_menus.button.addFieldPicker({
					parent: field,
					pickerId: "$tunnel",
					fontIcon: "tunnel",
					text: syra_local.fieldTunnel,
					shortCutTip: syra_shortCuts.tip.openTunnel,
					btnclick: this.onClick
				});
				if (!field.$isDisabled && field.$isReadOnly) {
					exports.button.hide(field.picker_$tunnel, false);
				}
			}
		},
		onClick: function() {
			var field = this.parent;
			syra_controller.executeMenu(field.$menus.$tunnel, field.isRecordArticle ? field : field.articleParent);
		}
	},
	$lookup: {
		add: function(field, $menu) {
			if (field.$isEditMode && !field.picker_$lookup) {
				syra_menus.button.addFieldPicker({
					parent: field,
					pickerId: "$lookup",
					fontIcon: "lookup",
					text: syra_local.fieldLookup,
					shortCutTip: syra_shortCuts.tip.openLookup,
					btnclick: this.onClick
				});
			}
			$menu.$isPopup = false;
		},
		onClick: function() {
			var field = this.parent;
			if ((field.articleParent && field.articleParent.onLookupClick) ? field.articleParent.onLookupClick(field) : true) {
				field.focus && field.focus();
				var $lookup = field.$menus.$lookup;
				if (field.isChildFieldRecord) {
					field.articleParent.currentSelectRecords = null;
				}
				syra_dlg.openLookup(field.boxParent, {
					article: field.articleParent,
					$url: $lookup.$url,
					onSelectRecord: function(selectedRecords) {
						if (!field.$isDisabled && !field.$isReadOnly) {
							if (field.isChildFieldRecord) {
								var record = selectedRecords[Object.keys(selectedRecords)[0]];
								field.articleParent.setDataBind(record.dataset);
								field.page.notifyDataChange(field.articleParent, record.dataset);
							} else {
								var record = selectedRecords[Object.keys(selectedRecords)[0]]; //single selection
								field.setDataValue($lookup.$result ? record.dataset[$lookup.$result] : record.dataset);
								field.focus();
								syra_workingCopy.notifyFieldChange(field, field.currentValue);
							}
						}
					}
				});
			}
		}
	}
};
exports.menus = {
	initialize: function(field) {
		field.$menus = {};
		field.$menusPopup = {};
		field.menuItems = {};
	},
	_applyMenusChange: function(field, $deltaMenu, $isAction, metaData) {
		var binds = Object.keys($deltaMenu);
		for (var ii = 0, jj = binds.length; ii < jj; ii++) {
			var $bind = binds[ii];
			var $menu = $deltaMenu[$bind];
			var created;
			if ($menu === null) {
				if (field.menuItems) {
					var menuItems = field.menuItems[$bind];
					if (menuItems) {
						for (var mm = 0, kk = menuItems.length; mm < kk; mm++) {
							field.removeItem(menuItems[mm]);
						}
						delete field.menuItems[$bind];
					}
				}
				delete field.$menus[$bind];
				delete field.$menusPopup[$bind];
			} else {
				if ($menu.$target == "embedded") {
					syra_fields.embedded.addEmbeddedLink(field, $bind, $menu);
					continue;
				}
				if (metaData && metaData.$diagnosePanelId) {
					$menu.$sourceBind = $menu.$sourceBind || $bind;
					$bind = metaData.$diagnosePanelId + "-" + $bind;
				}
				var $defined = field.$menus[$bind];
				if ($defined) {
					syra_delta.applyObjectDelta(field.page, $defined, $menu);
				} else {
					created = true;
					$menu.$isAction = $isAction;
					if (metaData && metaData.$diagnosePanelId) {
						$menu.$isPopup = false;
						field.loadNewItem(metaData.$diagnosesSlot, {
							$bind: $bind,
							$category: "link",
							$skin: "s-diagnose-link"
						});
					} else {
						$menu.$sourceBind = $menu.$sourceBind || $bind;
						$menu.$bind = field.isField ? (field.id + "-" + $bind) : $bind;
					}
					$defined = field.$menus[$bind] = $menu;
					if (field.isPopupMenuItem && !field.isPopupMenuItem($bind, $menu)) {
						$menu.$isPopup = false;
					} else {
						switch ($bind) {
							case "$first":
							case "$previous":
							case "$next":
							case "$last":
								field.isDesktopPage && _paging.addPagerLinksToPage(field, $bind);
								break;
							case "$edit":
								if (field.onAddMenuItem) {
									if (field.articleParent.$capability && field.articleParent.$capability.quickedit) {
										_quickEditBuilder.addPicker(field, $menu);
										$menu.$title = syra_local.fieldTunnel;
										$menu.$iconValue = "$tunnel";
										$menu.$isPopup = true;
									} else {
										$menu.$title = $menu.$title || syra_local.fieldEdit;
										field.onAddMenuItem($bind, $menu);
										$menu.$isPopup = false;
									}
								} else {
									$menu.$isPopup = true;
								}
								break;
							case "$canceledit":
								if (field.onAddMenuItem) {
									$menu.$title = $menu.$title || syra_local.fieldEdit;
									field.onAddMenuItem($bind, $menu);
									$menu.$isPopup = false;
								} else {
									$menu.$isPopup = true;
								}
								break;
							case "$delete":
								if (field.onAddMenuItem && field.$useDeletePicker) {
									$menu.$title = $menu.$title || syra_local.fieldDelete;
									field.onAddMenuItem($bind, $menu);
									$menu.$isPopup = false;
								} else {
									$menu.$isPopup = true;
								}
								break;
							case "remove_separator":
							case "add_separator":
								field.onAddMenuItem && field.onAddMenuItem($bind, $menu);
								$menu.$isPopup = false;

								break;
							case "$create":
							case "$select":
								$menu.$isPopup = !(field.isArrayField && field.onAddMenuItem($bind, $menu));
								break;
							case "$details":
								$menu.$isPopup = !field.isField || field.$isDetailLinkDisabled;
								break;
							default:
								var picker = exports.pickers[$bind];
								if (picker) {
									picker.add(field, $menu);
								} else {
									$menu.$isPopup = true;
								}
								break;
						}
					}

				}
				if ($defined.$isPopup && field.$menusPopup) {
					if ($menu.$isHidden) {
						delete field.$menusPopup[$bind];
					} else {
						field.$menusPopup[$bind] = $defined;
					}
				}
				if (field.menuItems) {
					var bounds = field.menuItems[$bind];
					if (bounds) {
						//if create setMenu is already done
						for (var mm = 0, kk = bounds.length; mm < kk; mm++) {
							bounds[mm].setMenu($menu, field.dataset);
						}
					} else {
						exports.group.addMenuItem(field.menusBag, $menu);
					}
				}
			}
		}
	},

	applyChange: function(field, metaData) {
		if (metaData) {
			if (field.variantItem) {
				field = field.variantItem;
			}
			if (field.isChildField) {
				this.applyChange(field.record, {
					$links: metaData.$links,
					$actions: metaData.$actions
				});
				return;
			}
			if (field.renderIcon) {
				metaData.$links !== undefined && field.renderIcon(metaData);
				return;
			}
			// in order not to show menu menu picker for unit ref field        
			if (field.$item && !field.$item.$isMenusHidden && (!field.$item.$isUnitField || field.$isEditMode)) {
				var $menus = (!field.isArrayField && metaData.$item) || metaData;
				if ($menus.$links !== undefined || $menus.$actions !== undefined) {
					if ($menus.$links === null) {
						this._clear(field);
					} else {
						$menus.$links && this._applyMenusChange(field, $menus.$links, false, metaData);
						$menus.$actions && this._applyMenusChange(field, $menus.$actions, true, metaData);
						if (!field.disposed && field.$item) {
							//!!check disposed as field can be disposed during apply ( field is page and menu is save, closing dlg)
							if (!field.picker_menus && !field.$item.$isFilterMode && !field.$item.$isPickerMenuHidden && field.$menusPopup && Object.keys(field.$menusPopup).length) {
								exports.pickers.menus.add(field);
								if (!field.$isDisabled && field.$isReadOnly) {
									exports.button.hide(field.picker_menus, false);
								}
							}
						}
					}
				}
			}
		}
	},
	_clear: function(item) {
		if (item.buttons) {
			for (var ii = 0, jj = item.buttons.length; ii < jj; ii++) {
				var btn = item.buttons[ii];
				btn.pickerId && exports.button.remove(btn);
			}
		}
		var $binds = Object.keys(item.menuItems);
		for (var ii = 0, jj = $binds.length; ii < jj; ii++) {
			var $bind = $binds[ii];
			var mn, items = item.menuItems[$bind];
			for (var kk = 0, mm = items.length; kk < mm; kk++) {
				mn = items[kk];
				if (mn.detailitemId) {
					var item = syra_store.get(mn.detailitemId);
					item && item.setDataValue(item.currentValue);
				}
				(mn.layoutParent ? mn.layoutParent : item).removeItem(mn, true);
			}
		}
		this.initialize(item);
		if (item.menusBox) {
			(item.menusBox.layoutParent ? item.menusBox.layoutParent : item).removeItem(item.menusBox, true);
			if (item.menusBag == item.menusBox) {
				delete item.menusBag;
			}
			delete item.menusBox;
		}
		exports.button.hide(item.picker_menus, true);
	},
	_disableItem: function(menuItem, isDisable) {
		menuItem && menuItem.disable(isDisable);
	},
	_disableItems: function(article, menuItems) {
		if (menuItems) {
			var isDisable = article.isPickersDisabled = article.$isReadOnly || article.$isDisabled;
			menuItems.$quickEdit && this._disableItem(menuItems.$quickEdit[0], isDisable);
			menuItems.$edit && this._disableItem(menuItems.$edit[0], isDisable);
			menuItems.$delete && this._disableItem(menuItems.$delete[0], isDisable);
			menuItems.$create && this._disableItem(menuItems.$create[0], isDisable);
		}
	}
};

exports.group = {
	//listMenuItem
	getMenuItems: function(group) {
		var items = [];
		for (var ii = 0; ii < group.boxChildItems.length; ii++) {
			if (group.boxChildItems[ii].$sourceBind != "$lazyload") { //(???)
				items.push(group.boxChildItems[ii]);
			}
		}
		return items;
	},
	_loadMenuItem: function(group, $menuItem, slot) {
		$menuItem.$category = "link";
		switch ($menuItem.$skin) {
			case "s-page-menus-link":
				$menuItem.$skin = "s-mn-main-link";
				break;
		}

		if (!$menuItem.$ownerId && group.$item.$ownerId) {
			$menuItem.$ownerId = group.$item.$ownerId;
		}
		if (!$menuItem.$skin) {
			if (group.$item.$isPopupContent) {
				$menuItem.$skin = "s-mn-link";
			} else {
				$menuItem.$skin = group.$item.$itemSkin || (group.$skin + "-link");
			}
		}
		if (group.$item.$itemIcon) {
			if ($menuItem.$icon === undefined) {
				$menuItem.$icon = {};
			}
			if (!$menuItem.$icon.$mode) {
				$menuItem.$icon.$mode = group.$item.$itemIcon.$mode;
			}
			if (!$menuItem.$icon.$path) {
				$menuItem.$icon.$path = group.$item.$itemIcon.$path;
			}
		}
		return group.page.loadNewItem(slot, $menuItem, group);
	},
	addMenuItem: function(group, $menu) {
		if (group) {
			var $item = group.$childMenus[$menu.$bind];
			if (!$item) {
				group.$childMenus[$menu.$bind] = $item = {
					$bind: $menu.$bind,
					$isAction: $menu.$isAction,
					$icon: $menu.$icon
				};
			}
			return this._loadMenuItem(group, $item, group.body, group);
		}
	},
	onBoxToggle: function(group) {
		if (group.$item) {
			var page = group.page;
			if (page.userPreferences && page.menuBar && !page.isVignettePage) {
				if (page.isInMenuBar(group) && !group.$item.$isPopupContent) {
					page.userPreferences.getForKey("$menuBarGroup", {})[group.id] = group.$opened;
					page.savePageDesign(true);
				}
			}
			group.openBox(group.$opened);
			if (group.$item.$isPopupContent) {
				if (!group.menusPopup) {
					group.body.className = "s-mn-popup-body";
					group.menusPopup = syra_dlg.openPopup(group, {
						content: group,
						slot: group.body,
						position: {
							my: "right top",
							at: "right bottom",
							of: $(group.domTitle)
						},
						onClose: function() {
							group.domTitle && syra_dom.toggleClass(group.domTitle, "s-close", true);
							group.$opened = false;
							group.menusPopup = null;
						}
					});
				} else {
					group.menusPopup.close();
				}
			}
		}
	},
	renderLayoutContent: function(group, $layout, slot) {
		if (!$layout) {
			//init
			group.childItems = [];
			$layout = group.$item.$layout;
			slot = group.body;
		}
		var cssRow, width;
		var newItem;
		for (var ii = 0, jj = $layout.$items.length; ii < jj; ii++) {
			var $child = $layout.$items[ii];
			newItem = null;
			if ($child.$bind) {
				if (!$child.$category || $child.$category == "link") {
					group.childItems.push(newItem = this._loadMenuItem(group, $child, slot));
				}
			} else {
				if ($child.$layout || $child.$category == "menus") {
					$child.$category = "menus";
					if ($child.$skin === undefined) {
						$child.$skin = group.$skin;
					}
					if (group.$item.$itemSkin && $child.$itemSkin === undefined) {
						$child.$itemSkin = group.$item.$itemSkin;
					}
					if (group.$item.$isBoxCollapsable && $child.$isBoxCollapsable === undefined) {
						$child.$isBoxCollapsable = group.$item.$isBoxCollapsable;
					}
					group.childItems.push(newItem = group.page.loadNewItem(slot, $child, group));
				} else
				if ($child.$layoutType) {
					newItem = document.createElement("div");
					group.renderLayoutContent($child, newItem);
					slot.appendChild(newItem);
				}
			}
			if (cssRow && newItem) {
				newItem = newItem.domItem || newItem;
				syra_dom.toggleClass(newItem, cssRow, true);
				if (width) {
					newItem.style.width = width;
				}
			}
		}
	},
	applyShortCut: function(group, shortcuts, event) {
		var current, items;
		if (shortcuts.enter && group._$selectedListIndex != undefined) {
			var menuItem = this.getMenuItems(group)[group._$selectedListIndex];
			if (menuItem) {
				menuItem.domItem.click();
				return true;
			}
		}
		if (shortcuts.up || shortcuts.down) {
			current = (group._$selectedListIndex != undefined) ? group._$selectedListIndex : -1;
			items = this.getMenuItems(group);
			if (shortcuts.down) {
				current++;
				if (current >= items.length) {
					current = 0;
				}
			} else {
				current--;
				if (current < 0) {
					current = items.length - 1;
				}
			}
			group._$selectedListIndex = Math.max(current, 0);
			for (var ii = 0, jj = items.length; ii < jj; ii++) {
				var menuItem = items[ii];
				if (menuItem.domItem) {
					syra_dom.toggleClass(menuItem.domItem, "s-selected", ii == menuGroup._$selectedListIndex);
				}
			}
			event.preventDefault();
			event.stopPropagation();
			return true;
		}
		return false;
	}
};

exports.applyArticleState = function(field) {
	var disabled = field.$isReadOnly || field.$isDisabled;
	if (field.isPickersDisabled != disabled) {
		field.isPickersDisabled = disabled;
		exports.button.disable(field.picker_$quickEdit, field.isPickersDisabled);
		exports.button.disable(field.picker_$edit, field.isPickersDisabled);
		if (!field.page.isFusionPage) {
			exports.button.disable(field.picker_$delete, field.isPickersDisabled);
			exports.button.disable(field.picker_$create, field.isPickersDisabled);
		}
		if (field.isSingleRecord) {
			var menuItems = field.menuItems;
			if (menuItems) {
				menuItems.$delete && menuItems.$delete[0] && menuItems.$delete[0].disable(field.isPickersDisabled);
			}
		}
	}
	exports.button.disable(field.picker_menus, field.$isDisabled);
};

exports.applyFieldState = function(field) {
	if (field.buttons) {
		for (var ii = 0, jj = field.buttons.length; ii < jj; ii++) {
			var btn = field.buttons[ii];
			if (btn.pickerId) {
				if (btn.pickerId == "$tunnel" || btn.pickerId == "menus") {
					exports.button.hide(btn, field.$isDisabled);
				} else {
					exports.button.hide(btn, field.$isDisabled || field.$isReadOnly);
				}
			}
		}
	}
	exports.menus._disableItems(field, field.menuItems);
};

exports.dispose = function(parent) {
	parent.menusPopup && parent.menusPopup.close();
	parent.menusPopup = parent.$menus = parent.$menusPopup = parent.menuItems = parent.pageSteps = null;
	exports.click.dispose(parent);
};