"use strict";
var helpers = require('syracuse-core/lib/helpers');
var MobileBox = require('./box').MobileBox;

function MobileSection(){

}

exports.MobileSection = helpers.defineClass(MobileSection, MobileBox, {
    loadBox: function(){
        this.$htmlTag = "section";
        this._ensureSkin(this.$authoringLevel = "section");
        MobileBox.prototype.loadBox.call(this);
    }
});

function MobileBlock(){

}

exports.MobileBlock = helpers.defineClass(MobileBlock, MobileBox, {
    loadBox: function(){
        this._ensureSkin(this.$authoringLevel = "block");
        MobileBox.prototype.loadBox.call(this);
    }
});
