"use strict";
var helpers = require('syracuse-core/lib/helpers');
var MobileBox = require('./box').MobileBox;

function MobileSectionBlock(){

}

exports.MobileSectionBlock = helpers.defineClass(MobileSection, MobileBox, {
    loadBox: function(){
        this._ensureSkin(this.$authoringLevel);
        MobileBox.prototype.loadBox.call(this);
    }
});

function MobileBlock(){

}

exports.MobileBlock = helpers.defineClass(MobileBlock, MobileBox, {
    loadBox: function(){
        this._ensureSkin(this.$authoringLevel = "block");
        MobileBox.prototype.loadBox.call(this);
    }
});
