"use strict";
var helpers = require('syracuse-core/lib/helpers');
var Field = require('syracuse-ui/lib/common/field/field').Field;
var commonField = require('syracuse-ui/lib/common/field/alphaNumField');
var fieldHelper = require('./fieldHelper');

function MobilePassword(){
}

exports.MobilePassword = helpers.defineClass(MobilePassword, Field, {
    getHtmlInput: function(){
        return "<input type='password'/>";
    },
    setDataValue: function(value, record){
        this.currentValue = value;
        if (this.$isEditMode) {
            this.$$input.val(this.currentValue);
        }
        else {
            this.$$dataValue.text((value || "").length > 0 ? "***" : "");
        }
    },
    loadBox: function(){
        fieldHelper.onLoadBox(Field, this);
    },
    setDescription: function(){
    },
    setHelp: function(){
    },
    _createValueContainer: function(){
        if (this.$isEditMode) {
            this.$$valueSlot = this.$$dataValue = this.$$item;
        }
        else {
            Field.prototype._createValueContainer.call(this);
        }
    }
});

