"use strict";
var helpers = require('syracuse-core/lib/helpers');
var commonField = require('syracuse-ui/lib/common/field/alphaNumField');
var fieldHelper = require('./fieldHelper');

function MobilePassword(){
}

exports.MobilePassword = helpers.defineClass(MobilePassword, commonField.PasswordField, {
    loadBox: function(){
        fieldHelper.onLoadBox(commonField.NumericField, this);
    },
    setDescription: function(){
    },
    setHelp: function(){
    },
    _createValueContainer: function(){
        if (this.$isEditMode) {
            this.$$valueSlot = this.$$dataValue = this.$$item;
        }
        else {
            commonField.PasswordField.prototype._createValueContainer.call(this);
        }
    }
});

