function MobileText(){
}

exports.MobileText = helpers.defineClass(MobileText, commonField.TextField, {
    loadBox: function(){
        fieldHelper.onLoadBox(commonField.TextField, this);
    },
    setDescription: function(){
    },
    setHelp: function(){
    },
    _createValueContainer: function(){
        if (this.$isEditMode) {
            this.$$valueSlot = this.$$dataValue = this.$$item;
        }
        else {
            commonField.TextField.prototype._createValueContainer.call(this);
        }
    },
    setDataValue: function(value, record){
        this.currentValue = value;
        if (this.$isEditMode) {
            this.$$input.val(this.currentValue);
        }
        else {
            this.$$dataValue.empty();
            if (value && this.$field.$links && this.$field.$links.$details && !this.$item.$isMenusDisabled) {
                this.appendDetailLink(this.$$dataValue, value, this.$field.$links.$details);
            }
            else {
                this.$$dataValue.text(value);
            }
        }
    }
});

