"use strict";
var helpers = require('syracuse-core/lib/helpers');
var DateTimeField = require('syracuse-ui/lib/common/field/datetimeField').DateTimeField;
var fieldHelper = require('./fieldHelper');

function MobileDateTime(){
}

exports.MobileDateTime = helpers.defineClass(MobileDateTime, DateTimeField, {
    loadBox: function(){
        fieldHelper.onLoadBox(DateTimeField,this);
    },
	 _createValueContainer: function(){
		if (this.$isEditMode) {
			this.$$value = this.$$item;
		}
		else{
			DateTimeField.prototype._createValueContainer.call(this);
		}
    },
});