"use strict";
var helpers = require("syracuse-core/lib/helpers");
var DesktopPage = require("syracuse-ui/lib/page/desktopPage").DesktopPage;

function MobileJSONPage() {}

exports.MobileJSONPage = helpers.defineClass(MobileJSONPage, DesktopPage, {
	loadBox: function($fieldsPrototype, initData) {
		this.$pageCategory = "page";
		this.$prototype = {
			$properties: {
				$fieldPrototype: {
					"$type": "application/x-string"
				},
				$fieldJSON: {
					"$type": "application/x-string"
				}
			},
			$actions: {
				$applyChange: {
					$title: syra_local.mobile_applychange
				}
			}
		};
		this.$item = {
			$layout: {
				$items: [{
					$skin: "s-mn-link",
					$category: "link",
					$bind: "$applyChange"

				}, {
					$layoutType: "tabs",
					$items: [{
						$category: "section",
						$title: syra_local.mobile_fieldJSON,
						$layout: {
							$items: [{
								$bind: "$fieldJSON",
								$isTitleHidden: true,
								$isEditMode: true,
								$rows: 50,
								$isAutoSizeDisabled: true,
								$skin: "s-json-field"
							}]
						}

					}, {
						$category: "section",
						$title: syra_local.mobile_fieldPrototype,
						$layout: {
							$items: [{
								$bind: "$fieldPrototype",
								$isTitleHidden: true,
								$isEditMode: true,
								$rows: 50,
								$isAutoSizeDisabled: true,
								$skin: "s-json-field"
							}]
						}
					}]
				}]
			}
		};
		DesktopPage.prototype.loadBox.call(this, initData);
		this.header.style.display = "none";
		this.body.style.padding = "0.5em";
	},
	notifyActionChange: function(menuItem, target, value) {
		if (value.$applyChange) {
			var diag = null;
			try {
				var field = this.boundFields.$fieldJSON[0];
				var $article = JSON.parse(field.getDataValue());
				field.setInputValue(JSON.stringify($article, null, 2));
				syra_site.mainPage.$item = $article;
				syra_site.mobileGateway.isApplyChangeDisabled = true;
				syra_site.mainPage.designer.historyTool.notifyUpdate(true);
				delete syra_site.mobileGateway.isApplyChangeDisabled;
				syra_site.mainPage.designer.historyTool.onAfterStepChange(helpers.object.clone($article, true));
				diag = {
					$message: syra_local.mobile_changeapplied,
					$severity: "success"
				};
			} catch (error) {
				diag = {
					$message: error.message
				};
			} finally {
				delete syra_site.mobileGateway.isApplyChangeDisabled;
				this.showDiagnoses({
					autoHide: diag.$severity == "success" ? {
						autoHideTimeOut: 3000
					} : null,
					$diagnoses: [diag]
				});
			}
			return;
		}
		DesktopPage.prototype.notifyActionChange.call(this, menuItem, target, value);
	},
	fillEditors: function($prototype, $article) {
		this.applyChange({
			$fieldJSON: JSON.stringify($article, null, 2),
			$fieldPrototype: JSON.stringify($prototype, null, 2)

		});
	}
});