"use strict";
var helpers = require('syracuse-core/lib/helpers');
var DesktopPage = require('syracuse-ui/lib/page/desktopPage').DesktopPage;

function MobilePage() {}

exports.MobilePage = helpers.defineClass(MobilePage, DesktopPage, {
	appendArticleMenus: function() {

	},
	appendHeader: function() {
		DesktopPage.prototype.appendHeader.call(this);
		this._headerFoooter.style.display = "none";
		if (this._headerDescription) {
			this._headerDescription.style.display = "none";
		}
	},
	loadBox: function(initData, $initDiagnoses) {
		DesktopPage.prototype.loadBox.call(this, initData, $initDiagnoses);
	}
});