"use strict";
var Sitemap = require('./sitemap').Sitemap;
var EditSitemap = require('./edit').EditSitemap;
var BreadCrumb = require('./breadcrumb').BreadCrumb;
exports.loadMegaMenus = function() {
	exports.breadcrumb = new BreadCrumb();
	exports.breadcrumb.loadBread();
};

exports.openSiteMap = function($prototype, dataset, editable) {
	syra_bookmarks.open(null, function() {
		var page = new(editable ? EditSitemap : Sitemap)($prototype, dataset);
		page.loadSitemap($prototype, dataset);
	});
};