"use strict";
var helpers = require('syracuse-core/lib/helpers');
var BarResizerDDAgent = require("./barResizerDDAgent").BarResizerDDAgent;

function _appplyPreferences(bar) {
	var defaultState;
	var defaultViewMode = bar.options.$viewMode || (syra_site.isTabletDevice ? "float" : "dock");
	if (syra_site.$item.$preferences) {
		defaultState = syra_site.$item.$preferences[bar.preferenceKey || bar.userPreferenceKey || "none"];
	}
	defaultState = defaultState || {
		$viewMode: defaultViewMode,
		$isCollapsed: false
	};
	if (bar.preferenceKey) {
		syra_site.getPreferences(bar.preferenceKey, defaultState, bar);
	} else {
		if (bar.userPreferenceKey) {
			defaultState = bar.page.userPreferences.getForKey(bar.userPreferenceKey) || defaultState;
		}
		syra_site.applyValues(bar, defaultState);
	}
	if (bar.options.$viewMode || !bar.$viewMode) {
		bar.$viewMode = defaultViewMode;
	}
}

function BarSplitter() {}

exports.BarSplitter = helpers.defineClass(BarSplitter, null, {
	load: function(page) {
		if (!this.id) {
			this.id = page.id + "-" + (++page._childItemOffset);
		}
		repository[this.id] = this;
		this.closeWidth = 100;
		this.minWidth = this.minWidth || 200;
		this.page = page;
		if (this.isSlotVisible === undefined) {
			this.isSlotVisible = true;
		}
		_appplyPreferences(this);
		this._bar = document.createElement("div");
		this._bar.className = this.options.$skins.bar;
		if (this.$width != undefined && parseInt(this.$width) <= this.closeWidth) {
			delete this.$width;
		}
		if (!this.options.$isResizerDisabled) {
			this.resizePicker = document.createElement("a");
			this.resizePicker.className = this.options.$skins.resizePicker;
			this.openerPicker = document.createElement("a");
			this.openerPicker.syraTool = this.id;
			this.openerPicker.syraOnClick = "onOpenerClick";
			this.openerPicker.className = this.options.$skins.openerPicker;
			this.page.setArticleId(this.openerPicker);
			this.resizePicker.appendChild(this.openerPicker);
			this.resizePicker.syraDragSpot = this.id;
			this._bar.appendChild(this.resizePicker);
			syra_dd.barResizer.push(this);
		}

		this._bar.appendChild(this.barBody);
		this.barSlot.appendChild(this._bar);
		this.page.setArticleId(this.barSlot);
		this.ensureState();
	},
	isDraggable: function(target, event) {
		if (target == this.resizePicker) {
			if (event.target != this.openerPicker) {
				syra_dd.dropableItem = {
					barSplitter: this,
					resizeDirection: this.options.resizeDirection
				};
				syra_dd.start(this, BarResizerDDAgent, this._bar);
				event.syraRetValue = false;
				return;
			}
		}
		return null;
	},
	toggleBar: function(show) {
		this.isHidden = !show;
		if (this.barSlot) {
			this.barSlot.style.display = this.isHidden ? "none" : "";
		}
	},
	ensureState: function(startResize) {
		if (!this.isHidden && !this._designing) {
			if (startResize && this.$isCollapsed) {
				this.curCollapsedState = this.$isCollapsed = false;
				delete this.$width;
				this.barSlot.style.width = this.barSlot.getBoundingClientRect().width + "px";
				this.barBody.style.display = "";
			} else {
				if (this.curCollapsedState != this.$isCollapsed) {
					if (this.$isCollapsed) {
						this.barSlot.style.width = "";
						this.barBody.style.display = "none";
					} else {
						this.$isCollapsed = false;
						this.barSlot.style.width = Math.max(this.$width || 0, this.minWidth) + "px";
						this.barBody.style.display = "";
					}
					if (this.options && this.options.resizeDirection) {
						if (this.curViewMode != this.$viewMode) {
							var direction = this.options.resizeDirection || "left";
							if (this.$viewMode == "float") {
								this.barSlot.style.display = "inline-block";
								this.barSlot.style.position = "absolute";
								this.barSlot.style.top = "0px";
								this.barSlot.style[direction] = 0;
								if (direction == "right") {
									this.page.scrollview.style.marginRight = "10px";
								} else {
									this.page.scrollview.style.marginLeft = "10px";
								}
								(this.page.vignetteField ? this.page.vignetteField.body : syra_site.body).appendChild(this.barSlot);
								syra_site.dom.setZIndex(this.barSlot);
							} else {
								this.barSlot.style.position = "";
								this.barSlot.style[direction] = "";
								this.barSlot.style.height = "";
								if (direction == "right") {
									this.page.scrollview.style.marginRight = "";
									this.page.dataSlot.parentNode.appendChild(this.barSlot);
								} else {
									this.page.scrollview.style.marginLeft = "";
									this.page.dataSlot.parentNode.insertBefore(this.barSlot, this.page.dataSlot);
								}
								this.barSlot.style.display = "";
							}
							this.curViewMode = this.$viewMode;
						}
					}
					this.curCollapsedState = this.$isCollapsed;
				}

			}
			syra_site.dom.toggleClass(this.barSlot, "s-close", this.$isCollapsed);
			if (this.resizePicker) {
				syra_site.dom.toggleClass(this.openerPicker, "s-close", this.$isCollapsed);
			}
		}
	},
	resizeBar: function() {
		if (this._bar && this.barSlot) {
			var height = this.page.barHeight || this.page.getScrollviewSize().height;
			if (height) {
				this._bar.style.height = height + "px";
				if (this.$viewMode == "float") {
					this.barSlot.style.height = height + "px";
				}
				if (this.resizePicker) {
					this.resizePicker.style.height = height + "px";
					this.openerPicker.style.top = ((height - this.openerPicker.clientHeight) / 2) + "px";
				}
				this.barBody.style.height = height + "px";
				if (this.designOverlay) {
					var style = this.designOverlay.style;
					var barRect = this._bar.getBoundingClientRect();
					style.top = barRect.top + "px";
					style.left = barRect.left + "px";
					style.height = barRect.height + "px";
					style.width = barRect.width + "px";
				}
				return true;
			}
		}
		return false;
	},
	collapse: function() {
		this.$isCollapsed = !this.$isCollapsed;
		this.savePreferences();
		this.ensureState();
		this.page.resizeArticle(true);
	},
	savePreferences: function(key) {
		if (this.preferenceKey) {
			syra_site.setPreferences(this.preferenceKey, this, ["$width", "$isCollapsed"]);
		} else {
			if (this.userPreferenceKey) {
				var pref = this.page.userPreferences.getForKey(this.userPreferenceKey, {});
				pref.$width = this.$width;
				pref.$isCollapsed = this.$isCollapsed;
				this.page.savePageDesign(true);
			}
		}
	},
	onOpenerClick: function() {
		this.collapse();
	},
	onPageResize: function() {
		if (!this.options.isAutoModeDisabled) {
			var autoViewMode = (window.innerWidth < 900 || (window.innerHeight > window.innerWidth)) ? "float" : "dock";
			if (this.autoViewMode == undefined) {
				if (this.$viewMode == autoViewMode) {
					this.autoViewMode = autoViewMode;
				}
			}
			if (this.autoViewMode != autoViewMode) {
				this.$viewMode = this.autoViewMode = autoViewMode;
				this.$isCollapsed = this.$viewMode == "float";
				this.ensureState();
			}
		}
		this.resizeBar();
	},
	dispose: function() {
		delete repository[this.id];
		if (this.resizePicker) {
			var ii = syra_dd.barResizer.indexOf(this);
			if (ii >= 0) {
				syra_dd.barResizer.splice(ii, 1);
			}
		}
		if (this.designOverlay) {
			syra_site.dom.removeChild(this.designOverlay);
			delete this.designOverlay;
		}
		if (this.barSlot && this.$viewMode == "float") {
			syra_site.dom.removeChild(this.barSlot);
		}
		this.page = this.barSlot = this.barBody = this._bar = this.resizePicker = this.openerPicker = null;
	}

});