"use strict";
exports.getItem = function() {
	var $skin = "s-localization-page";
	var localize = document.site.localize;
	return {
		$category: "page",
		$title: localize.lPageTitle,
		$layout: {
			$items: [{
				$format: "cards",
				$skin: $skin + "-skin",
				$isTitleHidden: true,
				$isPagerHidden: true,
				$isQuickDesignerEnabled: false,
				$bind: "$translations",
				$cards: {
					$skin: $skin + "-cards",
					$layout: {
						$items: [{
							$layoutType: "row",
							"$isFixedWidth": true,
							"$widths": "250,*",
							$items: [{
								$bind: "$localeChoice",
								$format: "$combo",
								$isTitleHidden: true,
								$title: localize.lPageLocaleTitle,
								$isPlaceHolder: true,
								$icon: {
									$mode: "iconText",
									$path: "flags_iso/"
								}
							}, {
								$bind: "$localeValue",
								$isTitleHidden: true
							}]
						}]
					}
				}
			}]
		}
	};
};