"use strict";
exports.getItem = function() {
	var $skin = "s-localization-page";
	var localize = document.site.localize;
	return {
		$category: "section",
		$title: localize.lPageTitle,
		$layout: {
			$items: [{
				$isTitleRowHidden: true,
				$isTitleHidden: true,
				$isPagerHidden: true,
				$isQuickDesignerEnabled: false,
				$isMenuRecordHidden: false,
				$bind: "$translations",
				$items: [{
					$bind: "$locale",
					$isTitleHidden: true
				}, {
					$bind: "$localeValue",
					$isTitleHidden: true
				}, {
					$category: "link",
					$bind: "$clearLocale",
					$css: $skin + "-clear-locale"
				}]
			}, {
				$bind: "$save",
				$category: "link"
			}]
		}
	};
};