"use strict";
exports.getItem = function() {
	var $skin = "s-localization-page";
	var localize = document.site.localize;
	return {
		$category: "page",
		$title: localize.lPageTitle,
		$layout: {
			$items: [{
				$format: "cards",
				$isTitleHidden: true,
				$isPagerHidden: true,
				$isQuickDesignerEnabled: false,
				$isMenuRecordHidden: true,
				$bind: "$translations",
				$cards: {
					$skin: $skin + "-cards",
					$layout: {
						$items: [{
							$layoutType: "row",
							$items: [{
								$bind: "$localeChoice",
								$format: "$combo",
								$isTitleHidden: true
							}, {
								$bind: "$localeValue",
								$isTitleHidden: true
							}, {
								$bind: "$clearLocale",
								$category: "link",
								$noText: true,
								$skin: $skin + "-remove-locale"
							}]
						}]
					}
				}
			}, {
				$bind: "$save",
				$category: "link",
				$skin: $skin + "-save"
			}]
		}
	};
};