"use strict";
exports.getItem = function() {
	var $skin = "s-localization-page";
	var localize = document.site.localize;
	return {
		$category: "page",
		$title: localize.lPageTitle,
		$layout: {
			$items: [{
				$format: "cards",
				$skin: $skin + "-skin",
				$isTitleHidden: true,
				$isPagerHidden: true,
				$isQuickDesignerEnabled: false,
				$bind: "$translations",
				$skinCard: $skin + "-cards",
				$layout: {
					$items: [{
						$layoutType: "row",
						$items: [{
							$bind: "$localeChoice",
							$format: "$combo",
							$isTitleHidden: true
						}, {
							$bind: "$localeValue",
							$isTitleHidden: true
						}]
					}]
				}
			}, {
				$bind: "$save",
				$category: "link",
				$skin: $skin + "-save"
			}]
		}
	};
};