"use strict";
exports.getItem = function() {
	var $skin = "s-localization-page";
	var localize = document.site.localize;
	return {
		$category: "page",
		$title: localize.lPageTitle,
		$layout: {
			$items: [{
				$format: "cards",
				$skin: $skin + "-skin",
				$isTitleHidden: true,
				$isPagerHidden: true,
				$isQuickDesignerEnabled: false,
				$bind: "$translations",
				$skinCard: $skin + "-cards",
				$layout: {
					$layoutType: "row",
					"$isFixedWidth": true,
					"$widths": "200,*",
					$items: [{
						$bind: "$localeChoice",
						$format: "$combo",
						$isTitleHidden: true,
						$icon: {
							$mode: "iconText",
							$path: "flags_iso/"
						}
					}, {
						$bind: "$localeValue",
						$isTitleHidden: true
					}]
				}
			}]
		}
	};
};