"use strict";
exports.getPrototype = function() {
	var localize = document.site.localize;
	return {
		$properties: {
			$translations: {
				$type: "application/x-array",
				$item: {
					$properties: {
						$locale: {
							$type: "application/x-choice",
							$title: localize.lPageLocale,
							$value: {
								$type: "application/x-integer",
								$enum: []
							}
						},
						$localeValue: {
							$type: "application/x-string",
							$title: localize.lPageField
						}
					},
					$links: {
						$clearLocale: {
							$title: localize.lPageClear
						}
					}
				}
			}
		},
		$links: {
			$save: {
				$title: localize.lPageSave
			}
		}
	};
};